import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';



function EvaluacionQuejaForm(){

const [getListaTipoConducta, setListaTipoConducta] = useState({ data: {} });

    useEffect(() => {

        async function fetchData() {
            ParametrosMasApi.getAllTiposConducta().then(
                data => {
                    if (!data.error) {
                        setListaTipoConducta(data)
                          console.log('jorenloz',data) 
                    }
                    else {
                        window.showModal(1);
                    }
                }
            )
        }

        fetchData();
    }, []);

    const componentSelectTipoConducta = () => {
        return (
            getListaTipoConducta.data.map((conducta, i) => {
                return (
                    <option key={conducta.id} value={conducta.id}>{conducta.attributes.conducta_nombre}</option>
                )
            })
        )
    }


//
const [getListaResultadoEvaluacion, setListaResultadoEvaluacion] = useState({ data: {} });

    useEffect(() => {

        async function fetchData() {
            ParametrosMasApi.getAllResultadoEvaluacion().then(
                data => {
                    if (!data.error) {
                        setListaResultadoEvaluacion(data)
                          console.log('jorenloz',data) 
                    }
                    else {
                        window.showModal(1);
                    }
                }
            )
        }

        fetchData();
    }, []);

    const componentSelectResultadoEvaluacion = () => {
        return (
            getListaResultadoEvaluacion.data.map((resultado, i) => {
                return (
                    <option key={resultado.id} value={resultado.id}>{resultado.attributes.nombre}</option>
                )
            })
        )
    }

//

    
const componentEvaluacionForm = () => {
    return(
            <>
            <Formik
                initialValues={{

                    }}
                validate={(valores) => {
                let errores ={};
                if(!valores.ResulEvaluacion){
                    valores.ResulEvaluacion = 'debe seleccionar un resultado';
                }
                return errores;

                }}
                onSubmit={(valores, {resetForm}) => {
                //funciones
                resetForm();
                }}>

                {({errors}) => (
                    <Form>
                        <div className='row'>
                            <div className='col-md-6'>
                                <label htmlFor='ResulEvaluacion'>Resultado de la Evaluación<span className='text-danger'></span></label>
                                <Field as='select' className='form-control' id='ResulEvaluacion' name='ResultEvaluacion'>
                                    <option value=''>Seleccione el Dato</option>
                                    { getListaResultadoEvaluacion.data.length > 0 ? componentSelectResultadoEvaluacion() : null }
                                </Field>
                                <ErrorMessage name='ResulEvaluacion' component={() => (<span className='text-danger'>{errors.ResulEvaluacion}</span>)}/>
                            </div>

                            <div className='col-md-6'>
                                <label htmlFor='NoticiaPrio'>Noticia Priorizada<span className='text-danger'></span></label>
                                <Field as='select' className='form-control' id='NoticiaPrio' name='NoticiaPrio'>
                                    <option value=''>Seleccione el Dato</option>
                                    <option value='1'>SI</option>
                                    <option value='2'>NO</option>
                                    
                                </Field>
                                <ErrorMessage name='NoticiaPrio' component={() => (<span className='text-danger'>{errors.NoticiaPrio}</span>)}/>
                            </div>

                            <div className='col-md-12'>
                                <label htmlFor='NoticiaPrio'>Tipo de Conducta<span className='text-danger'></span></label>
                                <Field as='select' className='form-control' id='NoticiaPrio' name='NoticiaPrio'>
                                    <option value=''>Seleccione el Dato</option>
                                    { getListaTipoConducta.data.length > 0 ? componentSelectTipoConducta() : null }
                                        
                                </Field>
                                <ErrorMessage name='NoticiaPrio' component={() => (<span className='text-danger'>{errors.NoticiaPrio}</span>)}/>
                            </div>

                            
                            <div className='col-md-12'>
                                        <label htmlFor='Justificacion'>Justificación y observaciones de la evaluación seleccionada *</label>
                                        <Field as="textarea" className="form-control" id="Justificacion" name="Justificacion" rows="4"
                                            placeholder="Escriba en este espacio las observaciones" /* maxLength={getMaximoTextArea} minLength={getMinimoTextArea} */></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{/* {countTextArea} / {getMaximoTextArea} */}</span>
                                        </div>
                                        <ErrorMessage name="Justificacion" component={() => (<span className="text-danger">{errors.Justificacion}</span>)} />
                                    </div>
                            

                            <div className='col-md-12'>
                                <div className='block-content block-content-full'>
                                    <div className='alert alert-success' role='alert'>
                                        <p className='mb-0'>Para continuar con la gestión de la presente queja, el resultado de la evaluación indicado por usted deberá ser previamente aprobado y avalado por el/la Delegado(a) de la dependencia.
                                        Por lo anterior al registrar la información del presente formulario, el SINPROC le será remitido automáticamente a dicho funcionario(a). </p>
                                    </div>
                                </div>
                            </div>

                             <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/ClasificacionRadicadoLista/`} /* state={{ from: from }} */>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>
                            
                            {/* <div className='col-md-12'>
                                <div className='block-content block-content-full'>
                                    <div className='alert alert-danger' role='alert'>
                                        <p className='mb-0'>Actualmente la dependencia DIRECCIÓN DE TECNOLOGÍAS DE INFORMACIÓN Y COMUNICACIÓN , no tiene parametrizado un funcionario con el rol de jefe, por tal motivo NO se puede continuar con el trámite del expediente. Por favor contacte a TIC y reporte la presente alerta. </p>
                                    </div>
                                </div>
                            </div> */}
                        </div>
                    </Form>
                )}   
        </Formik>
            </>
    );
}

const componentEvaluacionAprobacionForm = () => {
    return(
        <>
        <Formik
            initialValues={{

            }}
            validate={(valores) => {
            let errores ={};
            if(!valores.ResulEvaluacion){
            valores.ResulEvaluacion = 'debe seleccionar un resultado';
            }
            return errores;

            }}
            onSubmit={(valores, {resetForm}) => {
             //funciones
            resetForm();
            }}>

            {({errors}) => (
                
                    <div className='row'>
                        <div className='col-md-12'>
                            <label htmlFor='Justificacion'> *<span className='text-danger'></span></label>
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                            <thead>
                                <tr>
                                    <th>Fecha de Registro:</th>
                                    <td>23/09/2021 11:45:09</td>
                                </tr>
                                <tr>
                                    <th>Tipo de Conducta:</th>
                                    <td>CUMPLIMIENTO HORARIO</td>
                                </tr>
                                <tr>
                                    <th>Noticia Priorizada:</th>
                                    <td>NO</td>
                                </tr>
                                <tr>
                                    <th>Resultado Evaluación:</th>
                                    <td>INCORPORACIÓN</td>
                                </tr>
                                <tr>
                                    <th>Observación:</th>
                                    <td>REGISTRO DE QUEJA INCORPORACIÓN</td>
                                </tr>
                                <tr>
                                    <th>Registrado por:</th>
                                    <td>USUARIO USUARIO</td>
                                </tr>
                            </thead>
                            <tbody>
                            
                            </tbody>
                        </table>
                        
                        
                        </div>
                    
                        <div className='col-md-6'>
                            <label htmlFor='ConcepEvaluacion'>Concepto de la Evaluación<span className='text-danger'></span></label>
                            <Field as='select' className='form-control' id='ConcepEvaluacion' name='ConcepEvaluacion'>
                                <option value=''>Seleccione el Dato</option>
                                <option value='1'>APROBAR</option>
                                <option value='2'>RECHAZAR</option>
                            </Field>
                            <ErrorMessage name='ConcepEvaluacion' component={() => (<span className='text-danger'>{errors.ConcepEvaluacion}</span>)}/>
                        </div>

                        <div className='col-md-6'>
                            <label htmlFor='ReclasiEvaluacion'>Reclasificar el Resultado de Evaluación A.<span className='text-danger'></span></label>
                            <Field as='select' className='form-control' id='ReclasiEvaluacion' name='ReclasiEvaluacion'>
                                <option value=''>Seleccione el Dato</option>
                                <option value='1'>COMISORIO EJE [1]</option>
                                    <option value='2'>DEVOLUCION ENTIDAD [2]</option>
                                    <option value='3'>INCORPORACION [3]</option>
                                    <option value='5'>REMISION EXTERNO [5]</option>
                                    <option value='6'>REMISORIO INTERNO [6]</option>
                            </Field>
                            <ErrorMessage name='ReclasiEvaluacion' component={() => (<span className='text-danger'>{errors.ReclasiEvaluacion}</span>)}/>
                        </div>

                        <div className='col-md-12'>
                            <label htmlFor='DetalEvaluacion'>Detalle del concepto a la Evaluación<span className='text-danger'></span></label>
                            <Field  className='form-control' id='DetalEvaluacion' name='DetalEvaluacion'>
                                                                    
                            </Field>
                            <ErrorMessage name='DetalEvaluacion' component={() => (<span className='text-danger'>{errors.DetalEvaluacion}</span>)}/>
                        </div>


                        <div className='block-content block-content-full text-right'>
                            <button type='submit' className='btn btn-primary'>
                                <i className='fa fa-fw fa-save'></i>REGISTRAR
                            </button>
                        </div>
                        
                    </div>
                
            )}
        
    </Formik>

        </>
    );
}

    return(
    <>
    <div className="row"></div>
        <div className="col-md-12">
            <div className="block block-rounded block-bordered">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} /* state={{ from: from }} */><small>Ramas del proceso</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`}/*  state={{ from: from }} */><small>Evaluación del radicado</small></Link></li>
                        </ol>
                    </nav>
                </div>
            </div>
        </div>

        { }

        <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title"> SINPROC No {/* {radicado} */} :: <strong>Evaluación Radicado</strong></h3>
                        </div>
                        <div className='block block-themed'>
                            
                            <div className='block-content'>
                            
                                    {componentEvaluacionForm()}
                                    {/*componentEvaluacionAprobacionForm()*/}
                                
                            </div>
                        </div>
                        {/* <ClasificacionRadicado setIdProcesoDisciplinario={procesoDisciplinarioId} setIdEtapa={from.idEtapa} id="cr_componente" name="cr_componente" /> */}

                    </div>
        </div>


    </>
    );
}
export default EvaluacionQuejaForm;
