import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';

function AntecedentesForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getNombreUsuario, setNombreUsuario] = useState("");

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let dependencia = from.dependencia;

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/AntecedentesLista/`} state={{ from: from }} />;
    }


    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            obtenerParametros();
        }
        fetchData();
    }, []);

    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombres": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "created_user": getNombreUsuario,
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }
        console.log(JSON.stringify(data));
        AntecedentesApi.addAntecedente(data).then(
            datos => {
                if (!datos.error) {
                    from.debeConsultar = true;
                    window.showModal(2);
                    setIsRedirect(true);
                    from.estadoActividad = true;
                    window.showSpinner(false);
                }
                else {
                    setErrorApi(datos.error.toString());
                    window.showModal(1);
                    window.showSpinner(false);
                }
                
            }
        )



    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)


                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo '+getMinimoTextArea+' caracteres'
                    }
                    else if (valores.descripcion.length > getMaximoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo '+getMaximoTextArea+' caracteres'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">

                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from }}><small>Antecedentes</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Agregar Antecedente</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Crear Antecedente</strong></h3>
                            </div>
                            <div className="block-content">
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Descripción <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4"
                                            placeholder="Escriba en este espacio los antencedentes" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default AntecedentesForm;
