import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';
import { quitarAcentos } from '../Utils/Common';

function PortalLogLista() {

    const [getPortalLogLista, setPortalLogLista] = useState({ data: [] });
    const [getSeach, setSeach] = useState('');
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getEstadoLista, setEstadoLista] = useState('1');

    const handleCallback = (childData) => {
        try {
            setEstadoLista(childData)
        } catch (error) {

        }

    }

    const columns = [
        {
            name: 'FECHA',
            selector: p => p.attributes.fecha_registro ? p.attributes.fecha_registro : "",
            sortable: true,
            width: '20%'
        },
        {
            name: 'INTERESADO',
            selector: p => p.attributes.informacion_interesado.interesado ? p.attributes.informacion_interesado.interesado : "",
            sortable: true,
        },
        {
            name: 'DETALLE',
            selector: p => p.attributes.detalle ? p.attributes.detalle : "",
            sortable: true,
        },
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarPortalLog(1, paganationPerPages);
        }
        fetchData();
    }, []);

    const cargarPortalLog = (page, perPage) => {
        GenericApi.getAllGeneric('portal-log').then(
            datos => {
                if (!datos.error) {
                    setPortalLogLista(datos);
                    window.showSpinner(false);
                } else {
                    window.showModal();
                    window.showSpinner(false);
                }
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    return (
        <div>
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li className="breadcrumb-item"> <small>Administración</small></li>
                        <li className="breadcrumb-item"> <small>Portal Web</small></li>
                        <li className="breadcrumb-item"> <small>Log</small></li>
                    </ol>
                </nav>
            </div>
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">ADMINISTRACIÓN :: PORTAL WEB :: LOG</h3>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">
                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={getPortalLogLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if (suggestion.attributes.estado == getEstadoLista) {
                                            return suggestion;
                                        }
                                    } else if (
                                        ((suggestion.id
                                            + quitarAcentos(suggestion.attributes.created_at)
                                            + quitarAcentos(suggestion.attributes.informacion_interesado.interesado)
                                            + quitarAcentos(suggestion.attributes.detalle)
                                            + (suggestion.attributes.estado == "1" ? 'ACTIVO' : 'INACTIVO')).toLowerCase().includes(quitarAcentos(getSeach.toLowerCase()))
                                            && (suggestion.attributes.estado == getEstadoLista))
                                    ) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationTotalRows={getPortalLogLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />

                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default PortalLogLista;
