                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        console.log(JSON.stringify(data));

        ProcesoDiciplinarioApi.addProcesoDiciplinario(data).then(
            datos => {
                //console.log(JSON.stringify(data));

                if (!datos.error) {

                    setExitoApiCustom(datos["data"]["attributes"]["radicado"]);
                    window.showModal(3)
                    setIsRedirect(true);
