import { createSchema, addTranslations, setLanguageByName, tr } from 'react-hook-form-auto'

export default function GetEtapaModel(ddlData) {

    const model = createSchema('EtapaModel', {
        nombre: {
            type: 'string',
            required: true,
            minLength: 2,
            maxLength: 255,
        },
        id_tipo_proceso: {
            type: 'select',
            options: ddlData['mas-tipo-proceso'],
            required: true,
        },
        estado: {
            type: 'select',
            required: true,
            options: [
                { value: '1', label: 'Activo' },
                { value: '0', label: 'Inactivo' }
            ],
        }
    });
    return model;
}

setLanguageByName('es')
addTranslations({
    models: {
        EtapaModel: {
            nombre: 'Nombre',
            id_tipo_proceso: {
                _field: 'Tipo proceso',
                _default: "Seleccione",
            },
            estado: {
                _field: 'Estado',
                _default: "Seleccione",
            },
        },
    },
})
