                    // Se setea en true la respuesta
                    setRespuestaProcesoSirius(false);

                    // Se quita el cargando
                    window.showSpinner(false);

                    // Se setea el valor del mensaje para mostrar
                    setModalState({ title: "Validar proceso SINPROC", message: datos.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
