                                                <p className="mb-0"> * 15 caracteres.</p>
                                                <p className="mb-0"> * Incluir los dos (2) guiones "-".</p>
                                                <p className="mb-0">Ej: 2020-EE-0000002, 2020-ER-0000123</p>
                                            </div>
                                        </div>
                                    </div>

                                    <div className='row'>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <div className='row'>
                                                    <div className='col-md-12'>
                                                        <label>Documento adjunto .pdf</label>
                                                    </div>
                                                    <div className='col-md-12' style={{ marginLeft: '13px' }}>
                                                        <div className='row'>
                                                            <div className='col-md-10'>
                                                                <label className="custom-file-label" htmlFor="example-file-input-custom" data-toggle="custom-file-input">{getNombreDocumento}</label>
                                                                <input className="custom-file-input" data-toggle="custom-file-input" type="text" name="archivo" disabled value={getValueDocumento} />
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary" >
                                    Crear
                                </button>
                                <Link to={'/ParametroCampos'} className="font-size-h5 font-w600" >
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}

export default PortalNotificacionesForm;
