import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';

import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";



function ModalRemitirExpediente(props) {

    const [getListaDependenciaDestino, setListaDependenciaDestino] = useState({ data: {} });
    const [getRespuestaDependenciaDestino, setRespuestaDependenciaDestino] = useState(false);
    const [countTextArea, setCountTextArea] = useState(0);

    useEffect(() => {
        async function fetchData() {

            ParametrosMasApi.getAllTiposDependenciaOrigen().then(
                datos => !datos.error ? (setListaDependenciaDestino(datos), setRespuestaDependenciaDestino(true)) : window.showModal(1)
            )

        }
        fetchData();
    }, []);

    const selectDependenciaDestino = () => {
        return (
            getListaDependenciaDestino.data.map((dependencia, i) => {
                return (
                    <option key={dependencia.id} value={dependencia.id}>{dependencia.attributes.nombre}</option>
                )
            })
        )
    }

    return (
        <>
            <Formik
                initialValues={{
                    informacion: '',
                    dependenciaDestino: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    setCountTextArea(valores.informacion.length)
                    let errores = {}

                    if(!valores.dependenciaDestino){
                        errores.dependenciaDestino = 'Debe ingresar un valor'
                    }
                    if(!valores.informacion){
                        errores.informacion = 'Debe ingresar un valor'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    //enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="modal fade" id="modal-block-popout-remitir-proceso" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                            <div className="modal-dialog modal-lg" role="document">
                                <div className="modal-content">
                                    <div className="block block-themed block-transparent mb-0">
                                        <div className="block-header bg-primary-dark">
                                            <h3 className="block-title">REMITIR EL {props.object.nombre_tipo_expediente}: {props.object.radicado} VIGENCIA: {props.object.vigencia}</h3>
                                            <div className="block-options">
                                                <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                    <i className="fa fa-fw fa-times"></i>
                                                </button>
                                            </div>
                                        </div>
                                        <div className="block-content">
                                            <div className="row">
                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label htmlFor="dependenciaDestino">Seleccione la dependencia a remitir <span className="text-danger">*</span></label>
                                                        <Field as="select" className="form-control" id="dependenciaDestino" name="dependenciaDestino">
                                                            <option value="">Por favor seleccione</option>
                                                            {getRespuestaDependenciaDestino ? selectDependenciaDestino() : null}
                                                        </Field>
                                                        <ErrorMessage name="dependenciaDestino" component={() => (<span className="text-danger">{errors.dependenciaDestino}</span>)} />
                                                    </div>
                                                </div>

                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label htmlFor="informacion">Informaci贸n para su remisi贸n <span className="text-danger">*</span></label>
                                                        <Field as="textarea" className="form-control" id="informacion" name="informacion" rows="6" placeholder="Informaci贸n para su remisi贸n...." maxLength="4000"></Field>
                                                        <div className="text-right">
                                                            <span className="text-primary">{countTextArea} / 4000</span>
                                                        </div>
                                                        <ErrorMessage name="informacion" component={() => (<span className="text-danger">{errors.informacion}</span>)} />
                                                    </div>
                                                </div>
                                            </div>


                                        </div>
                                        <div className="block-content block-content-full text-right bg-light">
                                            <button type="submit" className="btn btn-sm btn-primary" >Remitir</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}

export default ModalRemitirExpediente;
