                                            <label htmlFor="inputNumeroDocumentoContratista">NÃºmero de documento <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" id="inputNumeroDocumentoContratista" name="inputNumeroDocumentoContratista" placeholder="" />
                                        </div>
                                    </div>

                                    <div className="col-md-3">
                                        <div className="form-group">
                                            <label htmlFor="inputRazonSocial">RazÃ³n Social <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" id="inputRazonSocial" name="inputRazonSocial" placeholder="" />
                                        </div>
                                    </div>

                                    <div className="col-md-3">
                                        <div className="form-group">
                                            <label htmlFor="inputNumeroContrato">NÃºmero de contrato </label>
                                            <Field as="input" className="form-control" id="inputNumeroContrato" name="inputNumeroContrato" placeholder="" />
                                        </div>
                                    </div>

