                            </Link>
                            </div>
                            

                        </Form>

                    </Formik>

                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <Formik
                    initialValues={{
                        observaciones:'',
                        tipo_respuesta: 2
                    }}
                    enableReinitialize

                    validate={(valores) => {
                        let errores = {}

