                                    <div className="col-lg-3"><span className="txt-blue-primary"><strong>Sector: </strong> <span className='txt-black-primary'> Ãrgano de control</span></span></div>
                                    <div className="col-lg-3"><span className="txt-blue-primary"><strong>DirecciÃ³n: </strong> <span className='txt-black-primary'> {from.datosQuejaInterna.direccion}</span></span></div>
                                    <div className="col-lg-3"><span className="txt-blue-primary"><strong>Correo: </strong> <span className='txt-black-primary'> {from.datosQuejaInterna.correo}</span></span></div>
                                    <hr/>
                                    <div className="col-lg-3"><span className="txt-blue-primary"><strong>TelÃ©fono: </strong> <span className='txt-black-primary'> {from.datosQuejaInterna.telefono}</span></span></div>
                                    <div className="col-lg-9"><span className="txt-blue-primary"><strong>PÃ¡gina web: </strong> <span className='txt-black-primary'> {from.datosQuejaInterna.paginaWeb}</span></span></div>
                                    <hr/>
                                    <div className="col-lg-9"><span className="txt-blue-primary"><strong>Ãltima actualizaciÃ³n: </strong> <span className='txt-black-primary'> {from.datosQuejaInterna.attributes.created_at}</span></span></div>

                                </div>
                                <div className="row">

                                </div>
                            </div>
                        </div>

                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={getListaFuncionariosRegistrados.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        return suggestion;
                                    } else if (

                                        (( suggestion.attributes.primer_nombre))

                                    ) {
                                        return suggestion;
                                    }
                                })}
