import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';
import DataTable from 'react-data-table-component';
import { quitarAcentos } from '../Utils/Common';

function CargosLista() {

    // Constantes generales
    const [getCargosLista, setCargosLista] = useState({ data: [] });
    const [getEstadoLista, setEstadoLista] = useState('1');
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);

    // Constantes de botones
    const [getRoutes, setRoutes] = useState({
        crear_registro: "/Cargos/Add",
        consultar_registros: "/Cargos",
        muestra_atras: false,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        muestra_inactivos: true,
    });

    // Metodo encargado de setear los valores inactivos
    const handleCallback = (childData) => {
        setEstadoLista(childData)
    }

    // Columnas de la tabla
    const columns = [
        {
            name: 'Nombre',
            selector: genero => genero.attributes.nombre,
            sortable: true,
            width: "80%"
        },
        {
            name: 'Estado',
            selector: genero => (genero.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "10%"
        },
        {
            name: 'Acciones',
            cell: row =>
                <div>
                    <Link to={`${row.id}`}>
                        <button type="button" className="btn btn btn-primary" title='Editar'>
                            <i className="fa fa-fw fa-edit"></i>
                        </button>
                    </Link>
                </div>,
            width: "10%"
        }
    ];

    // Se carga la informacion
    useEffect(() => {
        async function fetchData() {

            // Se llama el metodo encargado de cargar la informacion
            cargarCargos()
        }
        fetchData();
    }, []);

    // Metodo encargado de cargar los cargos sin estado
    const cargarCargos = () => {

        // Se consume la API
        GenericApi.getAllGeneric('cargos-sin-estado').then(

            // Se inicializa la variable de respuesta
            datos => {

                // Se valida que no haya error
                if (!datos.error) {

                    // Se setean los datos
                    setCargosLista(datos);
                }
            }
        )
    }

    const handlePageChange = page => {
        //window.showSpinner(true);
        setPageActual(page);
        //cargarFormatos(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        // window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        //  cargarFormatos(page, newPerPage);

    }

    return (
        <div>
            {<InfoErrorApi />}

            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li className="breadcrumb-item"> <small>Lista de Cargos</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">ADMINISTRACIÓN :: CARGOS {getEstadoLista == global.Constants.ESTADOS.INACTIVO ? "INACTIVOS" : "ACTIVOS"}</h3>

                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">
                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                        <ListaBotones getRoutes={getRoutes} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />

                    </div>
                    <div className="row">
                        <div className="col-sm-12">

                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={getCargosLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if (suggestion.attributes.estado == getEstadoLista) {
                                            return suggestion;
                                        }
                                    } else if (
                                        ((suggestion.id
                                            + quitarAcentos(suggestion.attributes.nombre)
                                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(quitarAcentos(getSeach.toLowerCase()))
                                            && (suggestion.attributes.estado == getEstadoLista))) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationTotalRows={getCargosLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CargosLista;
