import React, { useEffect, useState, } from 'react';
import { ErrorMessage, Form, Field, Formik } from 'formik';
import { Navigate } from 'react-router';
import { Link } from 'react-router-dom';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import Spinner from '../Utils/Spinner';
import PendientesApi from "./../Api/Services/PendientesApi";
import { getUser } from '../../components/Utils/Common';
import { ParametroModel } from '../Models/ParametroModel';

function MisPendientes() {


    const [respuestaPendientes, setRespuestaPendientes] = useState(false);
    const [listaPendientes, setListaPendientes] = useState({ data: [], links: [], meta: [] });

    const [respuestaPendientesTotal, setRespuestaPendientesTotal] = useState(false);
    const [listaPendientesTotal, setListaPendientesTotal] = useState({ data: [], links: [], meta: [] });


    const [errorApi, setErrorApi] = useState('');
    const [selectedRango, setSelectedRango] = useState("");
    const [nombreUsuario, setNombreUsuario] = useState("");
    const [casosActivos, setCasosActivos] = useState(0);


    const [getColorPrimary, setColorPrimary] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");



    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setNombreUsuario(getUser().nombre);

            PendientesApi.getAllPendientes().then(
                datos => {
                    if (!datos.error) {
                        setListaPendientes(datos)
                        setRespuestaPendientes(true)
                        setListaPendientesTotal(datos)
                        setRespuestaPendientesTotal(true)
                        setCasosActivos(datos.data.length)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);

    const componentListPendiente = () => {

        if (listaPendientes.data != null && typeof (listaPendientes.data) != 'undefined') {
            return (


                listaPendientes.data.map((pendiente, i) => {
                    return (
                        cargarTabla(pendiente, i)
                    )
                })
            )
        }
        else {
            return (

                listaPendientes.map((pendiente, i) => {
                    return (
                        cargarTabla(pendiente, i)
                    )
                })
            )
        }

    }

    const cargarTabla = (pendiente, i) => {
        return (
            <tr key={pendiente.id}>
                <td className="text-center">{i + 1}</td>
                <td className="font-w600">{pendiente.attributes.radicado}</td>
                <td className="d-none d-sm-table-cell">
                    <div className='row'>
                        <div className="col-md-12">
                            <small><strong>Fecha de vigencia:</strong> {pendiente.attributes.vigencia}</small><br />
                            <small><strong>Fecha de registro:</strong> {pendiente.attributes.created_at}</small><br />
                            <small><strong>SISTEMA DISCIPLINARIOS (Pendiente de Nombre)</strong></small><br />
                        </div>
                    </div>
                    <em className="text-muted"></em>
                </td>
                <td>{pendiente.attributes.dias_habiles}</td>
                <td>{pendiente.attributes.dias_calendario}</td>
                <td>
                    <div className="btn-group">
                        {/* <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Consultar Historial"><span className="fas fa-folder-open"> </span></button>
                                    <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Registrar Actuación"><span className="fas fa-chalkboard-teacher"> </span></button>
    
                                    <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Remitir Proceso"><span className="fas fa-file-import"> </span></button>
                                    <Link to="/RamasProceso" state={{ from: new ParametroModel(pendiente.attributes.radicado, pendiente.id, pendiente.attributes.vigencia)}}>
                                    <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Ver rama del proceso"><span className="fas fa-code-branch"> </span></button>
                                    </Link>
    
                                    <button type="button" className="btn btn-primary" data-toggle="tooltip" data-html="true" title="" data-original-title="Imprimir Caratula"><span className="fas fa-file-alt"></span></button>
    
                                    <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Remitir Proceso"><span className="fas fa-file-import"> </span></button> */}
                     
                        <Link to={`/RamasProceso/`} state={{ from: new ParametroModel(pendiente.attributes.radicado, pendiente.id, pendiente.attributes.vigencia,
                                                                true, getColorPrimary,getColorPrimary,getColorPrimary,getColorPrimary,getColorPrimary) }}>

                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Ver rama del proceso"><span className="fas fa-code-branch"> </span></button>
                        </Link>
                        {/* <button type="button" className="btn btn-primary" data-toggle="tooltip" data-html="true" title="" data-original-title="Imprimir Caratula"><span className="fas fa-file-alt"></span></button> */}

                    </div>
                </td>
            </tr>
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setListaPendientes(listaPendientesTotal);
            }
            else {

                setListaPendientes(listaPendientesTotal.data.filter(
                    suggestion => ((suggestion.attributes.radicado
                        + suggestion.attributes.vigencia + suggestion.attributes.created_at +
                        suggestion.attributes.dias_habiles + suggestion.attributes.dias_calendario).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };


    const enviarDatos = () => {

        window.showSpinner(true);
        setListaPendientes({ data: [], links: [], meta: [] });
        setRespuestaPendientes(false);
        var result = new Date();
        result.setDate(result.getDate() - selectedRango);
        let day = ("0" + result.getDate()).slice(-2);  //get day with slice to have double digit day
        let month = ("0" + (result.getMonth() + 1)).slice(-2); //get your zero in front of single month digits so you have 2 digit months
        let date = result.getFullYear() + '-' + month + '-' + day;


        const data = {
            "data": {
                "type": "pendietes",
                "attributes": {
                    "fecha": date,
                }
            }
        }

        //console.log(JSON.stringify(data));
        if (selectedRango != "-1") {
            PendientesApi.getAllPendientesFilter(data).then(
                datos => {
                    if (!datos.error) {
                        setListaPendientes(datos)
                        setRespuestaPendientes(true)
                        //console.log(datos)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )

        }
        else if (selectedRango == "-1") {
            PendientesApi.getAllPendientes().then(
                datos => {
                    if (!datos.error) {
                        setListaPendientes(datos)
                        setRespuestaPendientes(true)
                        //console.log(datos)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )
        }



    }
    return (
        <>

            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            {<Spinner />}

            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Listado de Pendientes</h3>
                                <label><i class="fas fa-user-clock"></i> Mis casos activos: {casosActivos}</label>
                            </div>

                            <div className="block-content block-content-full">
                                <div className="row">

                                    <div className="col-12">
                                        <div className="form-group">
                                            <label className="pr-2" htmlFor="rango">Filtrar por</label>
                                            <div className="btn-group">
                                                <button type="submit" onClick={() => setSelectedRango(7)} className="btn btn-outline-primary">
                                                    <i class="far fa-calendar-alt"></i> Últimos 7 días
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(1)} className="btn btn-outline-primary">
                                                    <i class="far fa-clock"></i> Desde ayer
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(0)} className="btn btn-outline-primary">
                                                    <i class="far fa-hand-point-down"></i> Hoy
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(-1)} className="btn btn-outline-primary">
                                                    <i class="fas fa-redo" ></i> Todo
                                                </button>
                                            </div>

                                        </div>
                                    </div>

                                </div>
                                <div className='row'>

                                    <div className='col-md-12'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="block-content block-content-full">
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th className="text-center">#</th>
                                                    <th className="text-center">SINPROC</th>
                                                    <th className="text-center">PROCESO</th>
                                                    <th className="text-center">DIAS HÁBILES</th>
                                                    <th className="text-center">DIAS CALENDARIO</th>
                                                    <th>ACCIONES</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {respuestaPendientes ? componentListPendiente() : null}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default MisPendientes;
