import React, { useEffect, useState } from 'react';
import 'rhfa-emergency-styles/dist/styles.css'
import Spinner from '../Utils/Spinner';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import ModalGen from '../Utils/Modals/ModalGeneric';
import { Navigate } from "react-router-dom";


function RolForm() {

    const [errorApi, setErrorApi] = useState('');
    const [getNombre, setNombre] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getListaFuncionalidades, setListaFuncionalidades] = useState({ data: {} });
    const [getRespuestaFuncionalidades, setRespuestaFuncionalidades] = useState(false);
    const [getFuncionalidadesAsociadas, setFuncionalidadesAsociadas] = useState([]);


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/Rol`} />;
    }


    useEffect(() => {
        window.showSpinner(true);
        async function fetchData() {
            getAllFuncionalidades();

        }

        fetchData();
    }, []);

    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if (name == "nombre") {
            setNombre(value);
        }

    }




    const getAllFuncionalidades = () => {
        GenericApi.getAllGeneric('modulo/get-modulos').then(
            datos => !datos.error ? (setListaFuncionalidades(datos), setRespuestaFuncionalidades(true),
                window.showSpinner(false)) : (window.showModal(1), window.showSpinner(false))
        )
    }

    const enviarDatos = () => {
        window.showSpinner(true);
        let data;

        data = {

            "data": {
                "type": "usuario",
                "attributes": {

                    "nombre": getNombre ? getNombre : "",
                    "funcionalidades": getFuncionalidadesAsociadas,

                }
            }
        }


        // console.log(JSON.stringify(data));

        GenericApi.addGeneric('role', data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2)
                    setIsRedirect(true);
                }
                else {
                    //console.log("hay error");
                    // console.log(datos.error);
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    const agregarCheck = (e, funcId) => {


        if (e == true) {

            setFuncionalidadesAsociadas(oldArray => [...oldArray,funcId] );

        }
        else {


            var index = getFuncionalidadesAsociadas.indexOf(funcId);
            if (index !== -1) {
                getFuncionalidadesAsociadas.splice(index, 1);
            }
        }



    }



    const listarFuncionalidades = () => {
        let mitadLista = (getListaFuncionalidades.data.length) / 2;
        return (


            <div className='row'>
                <div className="col-md-6">
                    {
                        getListaFuncionalidades.data.map((modulo, i) => {
                            return (
                                (i < mitadLista) ? (
                                    <div key={modulo.id} id="accordion" role="tablist" aria-multiselectable="true">
                                        <div className="block block-rounded mb-1">
                                            <div className="block-header block-header-default" role="tab" id="accordion_h1">
                                                <a className="font-w600" data-toggle="collapse" data-parent="#accordion" href="#accordion_q1" aria-expanded="true" aria-controls="accordion_q1"><i className="fas fa-folder" /> {modulo.attributes.nombre_mostrar} </a>
                                            </div>
                                            <div id="accordion_q1" className="collapse" role="tabpanel" aria-labelledby="accordion_h1" data-parent="#accordion">
                                                <div className="block-content">

                                                    {
                                                        modulo.attributes.funcionalidades.map((funcionalidad, ii) => {
                                                            return (

                                                                <div className='row' key={funcionalidad.id}>

                                                                    <div className="col-md-2" >
                                                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                                                            <input type="checkbox" onChange={e => agregarCheck(e.target.checked, funcionalidad.id)} className="custom-control-input" id={funcionalidad.id} name={funcionalidad.id} />
                                                                            <label className="custom-control-label" htmlFor={funcionalidad.id}></label>
                                                                        </div>
                                                                    </div>
                                                                    <div className="col-md-4" >
                                                                        {funcionalidad.nombre_mostrar}
                                                                    </div>

                                                                </div>


                                                            )
                                                        })
                                                    }

                                                </div>
                                            </div>
                                        </div>
                                    </div >
                                ) : null
                            )
                        })
                    }
                </div>
                <div className="col-md-6">
                    {
                        getListaFuncionalidades.data.map((modulo, i) => {
                            return (
                                (i >= mitadLista) ? (
                                    <div key={modulo.id} id="accordion" role="tablist" aria-multiselectable="true">
                                        <div className="block block-rounded mb-1">
                                            <div className="block-header block-header-default" role="tab" id="accordion_h1">
                                                <a className="font-w600" data-toggle="collapse" data-parent="#accordion" href="#accordion_q1" aria-expanded="true" aria-controls="accordion_q1"><i className="fas fa-folder" /> {modulo.attributes.nombre_mostrar} </a>
                                            </div>
                                            <div id="accordion_q1" className="collapse" role="tabpanel" aria-labelledby="accordion_h1" data-parent="#accordion">
                                                <div className="block-content">

                                                    {
                                                        modulo.attributes.funcionalidades.map((funcionalidad, ii) => {
                                                            return (

                                                                <div className='row' key={funcionalidad.id}>

                                                                    <div className="col-md-2" >
                                                                        <div className="custom-control custom-switch custom-control-lg mb-2">
                                                                            <input type="checkbox" onChange={e => agregarCheck(e.target.checked, funcionalidad.id)} className="custom-control-input" id={funcionalidad.id} name={funcionalidad.id} />
                                                                            <label className="custom-control-label" htmlFor={funcionalidad.id}></label>
                                                                        </div>
                                                                    </div>
                                                                    <div className="col-md-4" >
                                                                        {funcionalidad.nombre_mostrar}
                                                                    </div>

                                                                </div>


                                                            )
                                                        })
                                                    }

                                                </div>
                                            </div>
                                        </div>
                                    </div >
                                ) : null
                            )
                        })
                    }
                </div>
            </div>





        )
    }

    const componentFormularioUsuario = () => {

        return (
            <>


                <Spinner />
                <Formik
                    initialValues={{

                        nombre: '',
                        funcionalidades: ''

                    }}
                    enableReinitialize
                    validate={(valores) => {

                        let errores = {}

                        if (!getNombre) {
                            errores.nombre = "Debe ingresar un nombre";
                        }

                        if (getFuncionalidadesAsociadas == '') {

                            errores.funcionalidades = "Debe seleccionar al menos una funcionalidad";
                        }

                        return errores
                    }}
                    onSubmit={(valores, { resetForm }) => {

                        enviarDatos();
                    }}
                >
                    {({ errors }) => (
                        <Form>

                            <div className='row'>

                                <div className="col-md-12">
                                    <div className="block block-themed">
                                        <div className="block-header">
                                            <h3 className="block-title">Datos del Rol</h3>
                                        </div>

                                        <div className="block-content">
                                            <div className="row">

                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label htmlFor='nombre'>Nombre <span className="text-danger">*</span> </label>
                                                        <Field value={getNombre} type="text" id="nombre" name="nombre" className="form-control" placeholder="Nombre"
                                                            onChange={handleInputChange} />
                                                        <ErrorMessage name="nombre" component={() => (<span className="text-danger">{errors.nombre}</span>)} />
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="row" >

                                                <div className="col-md-12" >
                                                    <div className="form-group">
                                                        <label>Asignar funcionalidades <span className="text-danger">*</span></label>
                                                    </div>
                                                    <ErrorMessage name="funcionalidades" component={() => (<span className="text-danger">{errors.funcionalidades}</span>)} />
                                                    <div style={{ 'height': 'auto'}}>
                                                        {getRespuestaFuncionalidades ? listarFuncionalidades() : null}
                                                    </div>

                                                </div>
                                            </div>

                                        </div>
                                        <div className="block-content block-content-full text-right bg-light">
                                            <button type="submit" className="btn btn-rounded btn-primary">
                                                 Registrar
                                            </button>
                                        </div>

                                    </div>


                                </div>


                            </div>
                        </Form>
                    )}
                </Formik>
            </>
        )
    }

    return (

        <>
            {isRedirect ? redirectToRoutes() : null}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
           
                
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Rol`}><small>Lista de Roles</small></Link></li>
                                <li className="breadcrumb-item"> <small> Crear Rol</small></li>
                            </ol>
                        </nav>
                    </div>
                


            <div className="col-md-12">


                {componentFormularioUsuario()}

            </div>



        </>
    )

}
export default RolForm;
