import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom'
import GenericApi from '../../Api/Services/GenericApi';

function EntidadInvestigadoCambiarEstadoForm() {


    const [isRedirect, setIsRedirect] = useState(false);

    const [countTextArea, setCountTextArea] = useState(0);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    let { idEntidadInvestigado } = useParams();



    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/`} state={{ from: from }} />;
    }

    const [getErrorApi, setErrorApi] = useState('');
    const [getEntidadInvestigado, setEntidadInvestigado] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoEntidad, setRtaEntidad] = useState(false);
    const [getEstado, setEstado] = useState("activa");
    const [getNuevoEstado, setNuevoEstado] = useState(true);

    useEffect(() => {
        async function fetchData() {

            console.log(idEntidadInvestigado);

            GenericApi.getByIdGeneric('entidad-investigado', idEntidadInvestigado).then(
                datos => {

                    if (!datos.error) {
                        if (datos.data[0]) {
                            if (datos.data[0].attributes.estado == true) {
                                setEstado("inactiva");
                                setNuevoEstado(false);
                            }
                        }


                        setEntidadInvestigado(datos);
                        setRtaEntidad(true);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()

                    }

                }
            )

            obtenerParametros();

        }
        fetchData();
    }, []);

    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombres": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric('parametro/parametro-nombre',data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'entidad_investigado',
                "attributes": {
                    "observaciones": datos.descripcion,
                    "estado": getNuevoEstado,
                    "id_proceso_disciplinario":procesoDisciplinarioId
                }
            }
        }


        console.log(JSON.stringify(data));

        GenericApi.updateGeneric('entidad-investigado', idEntidadInvestigado, data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2);
                    setIsRedirect(true);
                    from.estadoActividad = true;
                }
                else {
                    setErrorApi(datos.error.toString());
                    window.showModal(1);
                }
                window.showSpinner(false);
            }
        )
    }


    const CargarEntidad = () => {

        if (getEntidadInvestigado.data != null && typeof (getEntidadInvestigado.data) != 'undefined') {
            return (
                getEntidadInvestigado.data.map((entidad, i) => {
                    return (
                        <tr key={entidad.id}>
                            <td>{entidad.attributes.nombre_entidad}</td>
                            <td>
                                <small><strong>Fecha de registro:</strong> {entidad.attributes.created_at}<br /></small>
                                <small><strong>Sector:</strong> Pendiente<br /></small>
                                <small><strong>Nombre investigado:</strong> {entidad.attributes.nombre_investigado}<br /></small>
                                <small><strong>Cargo investigado:</strong> {entidad.attributes.cargo}<br /></small>
                                <small><strong>Observaciones:</strong> {entidad.attributes.observaciones}<br /></small>
                                <small><strong>Registrado por:</strong> {entidad.attributes.created_user}<br /></small>
                                <small><strong>Registrado en:</strong> {entidad.attributes.nombre_entidad}<br /></small>
                            </td>

                        </tr>
                    )
                })


            )
        }

    }


    return (
        <>
            {isRedirect ? redirectToRoutes() : null}

            {<InfoErrorApi error={getErrorApi} />}
            {<InfoExitoApi />}

            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)
                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo de '+getMinimoTextArea+' caracteres'
                    }
                    else if (valores.descripcion.length > getMaximoTextArea) {
                        errores.descripcion = 'Debe ingresar máximpo de  '+getMaximoTextArea+' caracteres'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/EntidadInvestigadoLista/`} state={{ from: from }}><small>Lista Entidad del Investigado</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Cambiar estado de entidad del investigado</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Inactivar datos entidad del investigado</strong></h3>
                            </div>
                            <div className="block-content">


                                <div className='col-md-12'>

                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>

                                                <th>Entidad</th>
                                                <th>Detalle</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {getRtaInfoEntidad ? CargarEntidad() : null}

                                        </tbody>
                                    </table>
                                </div>
                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Motivo por el que {getEstado} el registro la entidad del investigado <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4"
                                            placeholder="Escriba en este espacio la observación" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default EntidadInvestigadoCambiarEstadoForm;
