import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { useParams } from "react-router";

function DatoInteresadoMigracionLista() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaDatosInteresado, setListaDatosInteresado] = useState({ data: [], links: [], meta: [] });

    let { radicado, version, id } = useParams();


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            listaAntecedentes();
            
           
        }
        fetchData();
    }, []);


    const listaAntecedentes= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-lista-interesados/"+radicado+"/"+version+"/"+id).then(
                datos => {

                    if (!datos.error) {
                        setListaDatosInteresado(datos);

                    } else {
                        setModalState({ title: "Migración Datos del interesado", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const lista = () => {

        if (getListaDatosInteresado.data != null && typeof (getListaDatosInteresado.data) != 'undefined') {
            return (

                getListaDatosInteresado.data.map((item, i) => {
                    return (
                        <tr>
                            <td>{i=i+1}</td>
                            <td>{item.attributes.interesado}</td>
                            <td>{item.attributes.identificacion_interesado}</td>
                            <td><Link className="text-dark" to={`/CargarMigracionInteresado/${radicado}/${version}/${id}/${item.attributes.id}`}><small>Completar información</small></Link></td>
                        </tr>
                    )
                })
            )
        }
    }


    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ProcesoDisciplinarioMigracion/${radicado}/${version}/${id}`}><small>Inicio proceso de migración</small></Link></li>
                        <li class="breadcrumb-item"> <small>Lista de interesados</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Proceso de migración</h3>
                </div>

                <div className="block-content">
                    
                    <div className="col-md-12">

                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col" style={{width: "60%"}}>Nombre</th>
                                    <th scope="col" style={{width: "20%"}}>Identificación</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                   {lista()}                 
                            </tbody>
                        </table>
                        
                    </div>
                </div>
                
            </div>

                   
        </>
    );
}


export default DatoInteresadoMigracionLista;
