import axios from 'axios'

const baseUrl = 'http://127.0.0.1:8000/api/v1/'

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

async function consultarRadicadoSirius(data){
    
    const url = baseUrl + "proceso-diciplinario/validar-sirius";
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        //console.log(response)
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error.message}
    });
}


async function consultarRadicadoDesgloce(data){
    
    const url = baseUrl + "proceso-diciplinario/validar-desglose";
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        //console.log(response)
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error.message}
    });
}

async function consultarRadicadoSinproc(data){
    
    const url = baseUrl + "proceso-diciplinario/validar-sinproc";
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        //console.log(response)
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error.message}
    });
}

async function addProcesoDiciplinario(data){

    const url = baseUrl + "proceso-diciplinario";
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        return response.data;
    })
    .catch(error => {
        //console.log(error);
        return {estado: false, error: error}
    });
}



export default { addProcesoDiciplinario, consultarRadicadoSirius, consultarRadicadoDesgloce, consultarRadicadoSinproc }
