import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';
import Spinner from '../Utils/Spinner';

function ConsecutivoDesgloseLista() {
    const [ConsecutivoDesgloseLista, setConsecutivoDesgloseLista] = useState({ data: [] });
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getSeach, setSeach] = useState('');

    const [getEstadoLista, setEstadoLista] = useState('1');
    
    const [getRoutes, setRoutes] = useState({

        crear_registro: "/ConsecutivoDesglose/Add",
        consultar_registros: "/ConsecutivoDesglose",
        muestra_atras: false,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        muestra_inactivos: true,
    });

    const handleCallback = (childData) => {
        try {
            setEstadoLista(childData);
        } catch (error) {

        }
    }

    const columns = [
        {
            name: 'Vigencia',
            selector: ConsecutivoDesglose => ConsecutivoDesglose.attributes.id_vigencia.vigencia,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Consecutivo',
            selector: ConsecutivoDesglose => ConsecutivoDesglose.attributes.consecutivo,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Estado',
            selector: ConsecutivoDesglose => (ConsecutivoDesglose.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "10%"
        },
        {
            name: 'Acciones',


            cell: row => 
            <div>
                <Link to={`/ConsecutivoDesglose/${row.id}`} state={{ from: row }}>
                    <button type="button" className="btn btn btn-primary" title='Editar'>
                        <i className="fa fa-fw fa-edit"></i>
                    </button>
                </Link>
            </div>,
            width: "10%"
        }
    ];

    useEffect(() => {
        async function fetchData() {

            // Se habilita el cargando
            window.showSpinner(true);

            cargarConsecutivoDesglose(1, paganationPerPages);

        }
        fetchData();
    }, []);

    const cargarConsecutivoDesglose = (page, perPage) => {
        GenericApi.getAllGeneric('mas_consecutivo_desglose').then(
            datos => {
                if (!datos.error) {
                    setConsecutivoDesgloseLista(datos);

                    // Se utiliza el cargando
                    window.showSpinner(false);
                }
                else{
                    window.showModal();
                }
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }


    return (
        <div>
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <small>Administración</small></li>
                        <li class="breadcrumb-item"> <small>Maestras</small></li>
                        <li className="breadcrumb-item"> <small>Lista de consecutivos de desglose</small></li>
                    </ol>
                </nav>
            </div>
            {<InfoErrorApi />}
            {<Spinner />}

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Consecutivos de desglose registrados {getEstadoLista == global.Constants.ESTADOS.INACTIVO ? "Inactivos" : "Activos"}</h3>

                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">
                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                        <ListaBotones getRoutes={getRoutes} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
        {console.log(ConsecutivoDesgloseLista)}
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}

                                data={ConsecutivoDesgloseLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if(suggestion.attributes.estado == getEstadoLista){
                                            return suggestion;
                                        }
                                    } else if (

                                        ((suggestion.id
                                            + suggestion.attributes.vigencia
                                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(getSeach.toLowerCase())
                                            && (suggestion.attributes.estado == getEstadoLista))

                                    ) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationTotalRows={ConsecutivoDesgloseLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Vigencia"
                                striped
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ConsecutivoDesgloseLista;
