    const handleInputChangeArchivos = (e, index, actualizar = 0, id = 0, uuid_actuacion = 0, tipo_archivo = "", valorTabla = "") => {

        const { name, files } = e.target;
        setTipoDocumentoSubir(valorTabla);

        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);

            var error = 0;

            if (tipo_archivo == "Documento inicial" && actualizar == 1 && extension != "docx") {
                setErrorApi("El archivo seleccionado no tiene un formato permitido debe ser .docx");
                window.showModal(1);
                error = 1;
                return false;
            } else if (tipo_archivo == "Documento definitivo" && actualizar == 1 && extension != "pdf") {
                setErrorApi("El archivo seleccionado no tiene un formato permitido, debe ser .pdf");
                window.showModal(1);
                error = 1;
                return false;
            } else if (tipo_archivo == "Documento definitivo" && actualizar == 0 && extension != "pdf") {
                setErrorApi("El archivo seleccionado no tiene un formato permitido, debe ser .pdf");
                window.showModal(1);
                error = 1;
                return false;
            }

            if (error == 0) {

                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                    list[index][name] = files[0];
                } else {
                    var list = [...inputActualizarArchivos];
                    list[index][name] = files[0];
                    setValidarAccion(1);
                }

                if (files[0]) {
                    list[index][name] = files[0];
                } else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].ext = "";
                    list[index].uuid = "";
                    if (actualizar == 0) {
                        setInputListArchivos(list);
                    } else {
                        setInputActualizarArchivos(list);
                    }
                    return false;
                }

                // Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].ext = extension;
                        list[index].uuid = id;
                        if (actualizar == 0) {
                            setInputListArchivos(list);
                        } else {
                            list[index].uuid_actuacion = uuid_actuacion;
                            setInputActualizarArchivos(list);
                            setValidarAccion(1);
                        }
                        setRtaInfoTrue(true);
                        window.showModal(9)
                    }
                })
                obtenerPesoTotalArchivos(list);
            } else {
                if (actualizar == 0) {
                    var list = [...inputListArchivos];
                } else {
                    var list = [...inputActualizarArchivos];
                }
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index].uuid = "";
                if (actualizar == 0) {
                    setInputListArchivos(list);
                } else {
                    list[index].uuid_actuacion = uuid_actuacion;
                    setInputActualizarArchivos(list);
                }
            }
        } else {
            if (actualizar == 0) {
                var list = [...inputListArchivos];
            } else {
                var list = [...inputActualizarArchivos];
            }
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            list[index].uuid = "";
            if (actualizar == 0) {
                setInputListArchivos(list);
            } else {
                setInputActualizarArchivos(list);
            }
            obtenerPesoTotalArchivos(list);
            return false;
        }
    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tamaÃ±o de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tamaÃ±o y elimine algunos adjuntos.')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

