import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useState } from 'react';
import AntecedentesApi from '../../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
function AntecedentesForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    let { procesoDisciplinarioId } = useParams();

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/AntecedentesLista/${procesoDisciplinarioId}`} />;
    }

    const enviarDatos = (datos) => {
        const data = {
            "data" : {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                }
            }
        }

        console.log(data);

        AntecedentesApi.addAntecedente(data).then(
            datos => {
                if(!datos.error){
                    window.showModal(2)
                    setIsRedirect(true)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }
      
    return(
            <>
                { isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
                <Formik
                    initialValues={{
                        descripcion: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
                        if(!valores.descripcion){
                            errores.descripcion = 'Debe ingresar un antecedente'
                        }
                        else if(valores.descripcion.length < 10){
                            errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                        }
                        else if(valores.descripcion.length > 4000){
                            errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                        }
                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        enviarDatos(valores);
                    }}
                >

                {({ errors }) => (
                    <Form>
 <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${errorApi}/${errorApi}`}><small>Ramas del proceso</small></Link></li>
									<li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/AntecedentesLista/${errorApi}/${errorApi}`}><small>Antecedentes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-primary"  to={`/AntecedentesLista/${errorApi}/${errorApi}`}><small>Agregar Antecedente</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                        <div className="block block-rounded block-bordered">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">SINPROC No 1614971 :: Antecedentes</h3>
                            </div>
                            <div className="block-content">
                                <div className="row justify-content-center py-sm-3 py-md-5">
                                    <div className="col-sm-10 col-md-8">
                                        <div className="form-group">
                                            <label>Descripción</label>
                                            <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" 
                                                    placeholder="Escriba en este espacio los antencedentes"><span className="text-primary">{countTextArea} / 4000</span>            </Field>
                                            <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                        </div>
                                    
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="button" className="btn btn-sm btn-light" data-dismiss="modal">Cerrar</button>
                                <button type="submit" className="btn btn-sm btn-primary" >Guardar</button>              
                            </div>
                        </div>
                    
                    </Form>
                )}
            </Formik>  
        </>                            
    );
}


export default AntecedentesForm;
