                                            <input type="text" className="form-control" id="inputNumeroContrato" name="inputNumeroContrato" placeholder="" />
                                        </div>
                                    </div>

                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="inputDependenciaContratista">Dependencia <span className="text-danger">*</span></label>
                                            <select className="form-control" id="inputDependenciaContratista" name="inputDependenciaContratista"
                                                value={selectedDependenciaC} onChange={e => selectChangeDependenciaC(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                {respuestaDependencia ? selectTipoDependencia() : null}
                                            </select>
                                        </div>
                                    </div>

                                </div>

                                <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                    <Link to={`/RamasProceso/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                    </Link>
                                </div>


                                </>:null
                            }


                            
                            

                        </Form>

                    </Formik>

                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <>
                    <Formik
                        initialValues={{
                            observaciones:'',
                            tipo_respuesta:2
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres invÃ¡lidos'
                            }

                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}>
                        
                        {({ errors }) => (
                        <Form>
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="form-group">
                                                                
                                        <label>Observaciones</label>  <span className="text-danger">*</span>
                                        <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                            placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                    </div>  
                                </div>
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/RamasProceso/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>

                            </div>
                        </Form>

                        )}

                    </Formik>
                   
                </>
            );
        }
    };

    // Componente entidad del investigado
    const componenteEntidadInvestigado = () => {

        return (
            <>
                {<Spinner />}
                {<ModalGen data={getModalState} />}

                <Formik
                    initialValues={{
                        ingresoPresuntoInvestigado:''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {};

                        return errores;
                    }}

                >

                    {({ errors }) => (
                        <Form>
                            <div className="row">
                                <div className="col">
                                    <div className="form-group">
                                        <div className="block-header block-header-default">
                                            <h3 className="block-title" style={{ color: 'black' }}>Datos bÃ¡sicos de la entidad</h3>
                                        </div>

                                        <div className="block block-rounded block-bordered">

                                            <div className="block-content block-content-full">
                                                <div className="row">
                                                    <div className="col-sm-12">
                                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                            <tbody>
                                                                <tr>
                                                                    <td style={{ width: '17%' }}>
                                                                        <b> Entidad</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.attributes.nombre_entidad} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b> Sector</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value="ORGANO DE CONTROL" disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b>  DirecciÃ³n</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.direccion} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b> Correo</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.correo} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td >
                                                                        <b>TelefÃ³no</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.telefono} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                                <tr>
                                                                    <td>
                                                                        <b>Pagina web</b>
                                                                    </td>
                                                                    <td colSpan="1" >
                                                                        <Field value={from.datosQuejaInterna.paginaWeb} disabled type="text" className="form-control" />
                                                                    </td>
                                                                </tr>
                                                            </tbody>
                                                        </table>
                                                        <div style={{ textAlign: '-webkit-center' }}>
                                                            <span><b>Ultima actualizaciÃ³n de datos de la entidad:</b> {from.datosQuejaInterna.attributes.created_at}</span>
                                                        </div>

                                                        <div className="block-content">
