                                                                            value={selectedTipoRespuesta} onChange={e => selectChangeTipoRespuesta(e.target.value)}>
                                                                            <option value="">Por favor seleccione</option>
                                                                            {respuestaTipoRespuesta ? selectTipoRespuesta() : null}
