import { Link } from "react-router-dom";

import { useForm, useFieldArray, Controller } from "react-hook-form";
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import { Navigate } from "react-router-dom";
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import { getUser } from '../../../components/Utils/Common';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';

function SoporteRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    let id_etapa = from.id_etapa;
    let id_fase = from.id_fase;

    const { control, fileInput, handleSubmit } = useForm();
    const { fields, append, remove } = useFieldArray({
        control,
        name: 'folios',
    });

    const [isRedirect, setIsRedirect] = useState(false);

    const [countTextArea, setCountTextArea] = useState(0);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const [getFaseApi, setFaseApi] = useState({ attributes: null });

    useEffect(() => {
        async function fetchData() {
            ParametrosMasApi.getFaseById(id_fase).then(
                datos => {
                    if (!datos.error) {
                        setFaseApi(datos.data);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/SoporteRadicadoLista/`} state={{ from: from }} />;
    }
    const [inputListArchivos, setInputListArchivos] = useState([{ folios: "", archivo: {}, filebase64: "" }]);

    /*Funciones*/
    // handle input change
    const handleInputChangeFolios = (e, index) => {
        const { name, value } = e.target;
        const list = [...inputListArchivos];
        list[index][name] = value;
        setInputListArchivos(list);
    };

    const handleInputChangeArchivos = (e, index) => {

        const { name, files } = e.target;
        const list = [...inputListArchivos];
        list[index][name] = files[0];
        if (files[0]) {
            list[index][name] = files[0];
        }
        else {
            list[index][name] = '';
            list[index].filebase64 = '';
            setInputListArchivos(list);
        }
        //setInputListArchivos(list);
        //Conversion a Base64
        Array.from(e.target.files).forEach(archivo => {
            var reader = new FileReader();
            reader.readAsDataURL(archivo);
            reader.onload = function () {
                var arrayAuxiliar = [];
                var base64 = reader.result;
                arrayAuxiliar = base64.split(',');
                //console.log(arrayAuxiliar[1]);
                list[index].filebase64 = arrayAuxiliar[1];
                setInputListArchivos(list);
            }
        })
    }

    // handle click event of the Remove button
    const handleRemoveClick = index => {
        const list = [...inputListArchivos];
        list.splice(index, 1);
        setInputListArchivos(list);
    };

    // handle click event of the Add button
    const handleAddClick = () => {
        setInputListArchivos([...inputListArchivos, { folios: "", archivo: {}, filebase64: "" }]);
    };

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    const handleOnSubmit = (valores) => {

        let band = false;

        // VALIDA QUE NO EXISTAN ARCHIVOS CON EL MISMO NOMBRE
        for (let i = 0; i < inputListArchivos.length - 1; i++) {
            for (let j = i + 1; j < inputListArchivos.length; j++) {
                if (inputListArchivos[i].archivo.name === inputListArchivos[j].archivo.name) {
                    setErrorApi("No puede registrar archivos con el mismo nombre.")
                    window.showModal(1)
                    band = true;
                }
            }
        }

        if (band === false) {

            // PASO 1. VALIDAR CON SIRIUS
            // PASO 1.1 LOGIN Y RADICACION

            const attributes = [];

            for (let i = 0; i < inputListArchivos.length; i++) {
                // PASO 2. GUARDAR EN BASE DE DATOS
                if (inputListArchivos[i].folios !== null || inputListArchivos[i].folios !== '') {

                    attributes.push({
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": from.id_etapa,
                        "id_fase": from.id_fase,
                        "url_archivo": "www.prueba.com",
                        "nombre_archivo": inputListArchivos[i].archivo.name,
                        "file64": inputListArchivos[i].filebase64,
                        "estado": "1",
                        "num_folios": inputListArchivos[i].folios,
                        "num_radicado": radicado,
                        "vigencia": vigencia,
                        "extension": getFileExtension(inputListArchivos[i].archivo.name),
                        "peso": inputListArchivos[i].archivo.length,
                        "created_user": nombreUsuario,
                        "descripcion": valores.descripcion,                        
                        "es_compulsa": false,
                    });

                }
            }



            const data = {
                "data": {
                    "type": "documento_sirius",
                    "attributes": attributes
                }
            }

            DocumentoSiriusApi.addDocumentoSirius(data).then(
                datos => {
                    if (!datos.error) {
                        from.cambiaColorSoporteRadicado = "btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left";
                        window.showModal(2)
                        setIsRedirect(true);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )

        }

    }

    /*Componentes*/
    const componentFormSoporte = () => {
        return (
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    setCountTextArea (valores.descripcion.length);
                    let errores = {}

                    if (!valores.descripcion) {
                        errores.descripcion = 'Campo obligatorio'
                    }
                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {
                    handleOnSubmit(valores);
                }}>
                {({ errors }) => (
                    <Form>
                        {
                            inputListArchivos.map((x, i) => {
                                return (
                                    <div className="box" key={i}>
                                        <ul>
                                            <div className='row'>
                                                <div className='col-md-9'>
                                                    <div className='row'>
                                                        <div className='col-md-5'>
                                                            <input className='form-control' name="folios" placeholder="No. de folios" value={x.folios} onChange={e => handleInputChangeFolios(e, i)} required />
                                                        </div>

                                                        <div className='col-md-5'>
                                                            <label className="custom-file-label" htmlFor="example-file-input-custom" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                            <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i)} required />
                                                        </div>
                                                        {
                                                            inputListArchivos.length !== 1
                                                                ?
                                                                (
                                                                    <div className="col-md-2">
                                                                        <button className="btn btn-danger" onClick={() => handleRemoveClick(i)}><i className="fas fa-trash-alt col"></i></button>
                                                                    </div>
                                                                )
                                                                :
                                                                null
                                                        }

                                                    </div>
                                                </div>
                                                <div className='col-md-3'>
                                                    <div className='row'>
                                                        {
                                                            inputListArchivos.length - 1 === i
                                                                ?
                                                                (
                                                                    <div className="col-md-12">
                                                                        <button type="button" className="btn btn-sm btn-success mr-1 mb-3" onClick={handleAddClick}> <i className="fas fa-plus"></i> Agregar nuevo archivo</button>
                                                                    </div>
                                                                )
                                                                :
                                                                null
                                                        }
                                                    </div>
                                                </div>
                                            </div>
                                        </ul>
                                    </div>
                                );
                            })
                        }
                        <div className='row'>
                            <div className="col-md-9" style={{ paddingLeft: "55px" }}>
                                <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Descripción de los soportes" required  maxLength="4000"></Field>
                                <div className="text-right">
                                    <span className="text-primary">{countTextArea} / 4000</span>
                                </div>
                                <ErrorMessage name='descripcion' component={() => (<span className='text-danger'>{errors.descripcion}</span>)} />
                            </div>
                        </div>

                        <div className='row'>
                            <div className="col-md-12 text-right">
                                <br />
                                {/*<button type="submit" className="btn btn-primary" onClick={() => handleOnSubmit()}> <i className="fas fa-save"></i> Registrar</button>*/}
                                <button type="submit" className="btn btn-primary"> <i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/RamasProceso/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                                <br /><br />
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        );
    }

    return (
        <>
    
            {isRedirect ? redirectToRoutes() : null}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li className="breadcrumb-item"> <small>Soportes del radicado</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

           

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">SINPROC No {radicado} :: Crear {getFaseApi.attributes != null ? getFaseApi.attributes.nombre : null} </h3>
                </div>

                <div className="block-content">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="alert alert-warning alert-dismissable" role="alert">
                                <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   SOPORTE(S) DEL RADICADO </p>
                                <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                <p className="mb-0">
                                    Se recomienda que el nombre del archivo lleve la siguiente estructura Ejm: 2022-ER-0000018-1, 2022-EE-0000018-2
                                </p>
                                <p className="mb-0">
                                    NO puede superar QUINCE (15) Mb de peso/tamaño.
                                    Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                </p>
                                <p className="mb-0">
                                    El nombre del archivo debe ser de máximo 40 caracteres.
                                </p>
                            </div>
                        </div>
                    </div>
                    <div>
                        {componentFormSoporte()}
                    </div>
                </div>
            </div>
        </>
    );
}
export default SoporteRadicadoForm;
