import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";

import { useForm, useFieldArray, Controller } from "react-hook-form";


function SoporteRadicadoForm() {

    
    let { radicado, procesoDisciplinarioId } = useParams();


    const {control, handleSubmit} = useForm();
  const {fields, append, remove} = useFieldArray({
    control,
    name: 'books',
  });


  const handleOnSubmit = (values) => {

        let data;
        const { folios} = values;
        const filteredArr = folios.map(({ value }) => {return value;});


        const { files} = values;
        const filteredArr2 = files.map(({ value }) => {return value;});

        folios.forEach(function(element) {
         
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                
                    }
                }
            }

            console.log(data);

        });


        





    console.log(filteredArr, filteredArr2);
  };


    return (
        <>

            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                        to={`/MisPendientes`}>
                                    <small>Mis Pendientes</small>
                                </Link></li>
                                <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                        to={`/RamasProceso/${radicado}/${radicado}`}>
                                    <small>Ramas del proceso</small>
                                </Link></li>
                                <li className="breadcrumb-item"><small>Soporte del radicado</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>



            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Soporte del radicado SINPROC No. {radicado}</h3>
                </div>

                <div className="block-content">
                    <div className="row">

                        <div className="col-md-12 text-right">

                            <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                    <i className="fa fa-fw fa-archive mr-1"></i> Ver Historial
                                </button>
                            </Link>
                        </div>

                        <div className="col-md-12">
                            <div className="alert alert-warning alert-dismissable" role="alert">
                                <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   SOPORTE(S) DEL RADICADO </p>
                                <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                <p className="mb-0">
                                    Se recomienda que el nombre del archivo lleve la siguiente estructura Ejm: 2022-ER-0000018-1, 2022-EE-0000018-2
                                </p>
                                <p className="mb-0">
                                    NO puede superar QUINCE (15) Mb de peso/tamaño.
                                    Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                </p>
                                <p className="mb-0">
                                    El nombre del archivo debe ser de máximo 40 caracteres.
                                </p>
                            </div>
                        </div>

                    </div>


                    <div>

                        <Formik
                            initialValues={{
                                numFolios: '',
                                fileInput: ''
                            }}
                            enableReinitialize
                            validate={(data) => {

                                let errores = {}

                                if(!data.numFolios){
                                    errores.numFolios = 'Campo obligatorio'
                                }

                                if(!data.fileInput){
                                    errores.fileInput = 'Campo obligatorio'
                                }

                                return errores
                            }}
                            onSubmit={(data, {resetForm}) => {
                                console.log (data);
                                //enviarDatos(valores);
                            }}
                        >



                        {/* We are just gonna console log our data for now */}
                        <form onSubmit={handleSubmit((data) => handleOnSubmit(data))}>

                            <div className="col-md-12 text-right">
                                <button type="button" className="btn btn-sm btn-success mr-1 mb-3" onClick={() => append({ value: "" })}> <i class="fas fa-plus"></i> Agregar nuevo archivo</button>
                            </div>

                            <div className="col-md-12">
                                <ul>
                                    {/* Here we loop thru fields array and render each field as item, and we get the index as a second parameter */}
                                    {fields.map((item, index) => (
                                        // Make sure you set the key to something unqiue
                                        <li key={item.id}>

                                            <div className="row" style={{paddingTop: "10px"}}>

                                                <div className="col-md-4">
                                                    <Controller name={`folios.${index}.value`} control={control} defaultValue={item.value}
                                                        render={({field}) => 
                                                        <input  className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" {...field} />
                                                    
                                                    }
                                                    />
                                                </div>

                                                <div className="col-md-6">
                                                    <Controller name={`files.${index}.value`} control={control} defaultValue={item.value} 
                                                        render={({field}) => 
                                                        <div>
                                                            <label className="custom-file-label" for="example-file-input-custom" data-toggle="custom-file-input" id="fileInput" name="fileInput">Selecciona el archivo</label>
                                                            <input type="file" className="custom-file-input" data-toggle="custom-file-input" {...field}></input>
                                                        </div>
                                                    }
                                                    />
                                                </div>

                                                <div className="col-md-2">
                                                    <button className="btn btn-danger" onClick={() => remove(index)}><i className="fas fa-trash-alt col"></i></button>
                                                </div>
                                            </div>
                                        </li>

                                    ))}
                                </ul>
                            </div>

                            <div className="col-md-12 text-right">
                                <button type="submit" className="btn btn-sm btn-primary mr-1 mb-3" onClick={() => append({ value: "" })}> <i class="fas fa-save"></i> Registrar</button>
                            </div>
                            
                                
                        </form>

                        </Formik> 

                    </div> 
                </div>
            </div>

        </>                 
    );
  
}
export default SoporteRadicadoForm;
