import axios from 'axios'

const baseUrl = 'http://127.0.0.1:8000/api/v1/'

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

async function addClasificacionRadicado(data){

    const url = baseUrl + "clasificacion-radicado";
   
    console.log(url);

    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}


async function getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId){

    const url = baseUrl + "clasificacion-radicado/lista-clasificacion-radicado-por-id/" + procesoDisciplinarioId;
   
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        //console.log(response.data);
        return response.data;
    })
    .catch(error => {
        //console.log(error);
        return {estado: false, error: error}
    });
}

export default { addClasificacionRadicado, getAllClasificacionRadicadoByIdProDisciplinario }
