import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";

function ClasificacionRadicadoLista() {

    const [respuestaClasificacionRadicado, setRespuestaClasificacionRadicado] = useState(false);
    const [clasificacionRadicadoLista, setClasificacionRadicadoLista] = useState({ data: [], links: [], meta: [] });
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data:{} });
    const [respuestaTipoExpediente, setTipoExpediente] = useState(false);

    const [listaTipoQueja, setListaTipoQueja] = useState({ data:{} });
    const [respuestaTipoQueja, setTipoQueja] = useState(false);
    
    const [listaTerminoRespuesta, setListaTerminoRespuesta] = useState({ data:{} });
    const [respuestaTerminoRespuesta, setTerminoRespuesta] = useState(false);

    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/ClasificacionRadicadoLista/${procesoDisciplinarioId}`} />;
    }

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            /*ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setClasificacionRadicadoLista(datos)
                        setRespuestaClasificacionRadicado(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )*/


            ParametrosMasApi.getAllTiposExpediente().then(
                datos => !datos.error ? (setListaTipoExpediente(datos), setTipoExpediente(true) ) : window.showModal(1)
            )  
            
            
            ParametrosMasApi.getAllTiposQueja().then(
                datos => !datos.error ? (setListaTipoQueja(datos), setTipoQueja(true) ) : window.showModal(1)
            ) 

            ParametrosMasApi.getAllTerminosRespuesta().then(
                datos => !datos.error ? (setListaTerminoRespuesta(datos), setTerminoRespuesta(true) ) : window.showModal(1)
            ) 
        }
        fetchData();
    }, []);

    const selectTipoExpediente = () => {
        return (
            listaTipoExpediente.data.map((exp, i) => {
                return (
                    <option key={exp.id} value={exp.id}>{exp.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoQueja = () => {
        return (
            listaTipoQueja.data.map((queja, i) => {
                return (
                    <option key={queja.id} value={queja.id}>{queja.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTerminoRespuesta = () => {
        return (
            listaTerminoRespuesta.data.map((termino, i) => {
                return (
                    <option key={termino.id} value={termino.id}>{termino.attributes.nombre}</option>
                )
            })
        )
    }

    const listaClasificacionRadicado = () => {

        return(
            clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                return (
                    <tr key={clasificacion_radicado.id}>
                        <td className="text-center">{i+1}</td>
                        <td>{ clasificacion_radicado.attributes.expediente.nombre }</td>
                        <td>PENDIENTE</td>
                        <td>PENDIENTE</td>
                        <td>{clasificacion_radicado.attributes.created_at}</td>
                        <td>{clasificacion_radicado.attributes.estado ? 'Activo' : 'Inactivo'}</td>
                        <td>PENDIENTE</td>
                        
                       
                    </tr>
                )
            })
        )
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        setClasificacionRadicadoLista({ data: [], links: [], meta: [] });
        setRespuestaClasificacionRadicado(false);

        const data = {
            "data" : {
                "type": "clasificacion_radicado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": 1,
                    "id_tipo_expediente": datos.tipoExpediente?datos.tipoExpediente:"-1",
                    "observaciones":  datos.observaciones?datos.observaciones:"-1",
                    "id_tipo_queja": datos.tipoQueja?datos.tipoQueja:"-1",
                    "id_termino_respuesta": datos.terminoRespuesta?datos.terminoRespuesta:"-1",
                    "fecha_termino": null,
                    "hora_termino": 0,
                    "gestion_juridica": false,
                    "estado": true,
                    "id_estado_reparto": 2
                }
            }
        }

        //console.log(JSON.stringify(data));



        ClasificacionRadicadoApi.getAllClasificacionRadicadoByFilter(data, procesoDisciplinarioId).then(
            datos => {
                if(!datos.error){
                    setClasificacionRadicadoLista(datos)
                    setRespuestaClasificacionRadicado(true)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )

        

    }


    return(
        <>
        <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
    
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/ClasificacionRadicadoForm/${radicado}/${procesoDisciplinarioId}`}><small>Clasificación del radicado</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page"  to={`/ClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}><small> Historial</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                { isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
                <Formik
                    initialValues={{
                        tipoExpediente: '',
                        tipoQueja: '',
                        terminoRespuesta: '',
                        observaciones: ''
                    }}
                    enableReinitialize
                    validate={(valores) => {
                        let errores = {}
       
                        if((!valores.tipoExpediente || typeof(valores.tipoExpediente) == 'undefined')
                        && (!valores.tipoQueja || typeof(valores.tipoQueja) == 'undefined')
                        && (!valores.terminoRespuesta || typeof(valores.terminoRespuesta) == 'undefined')
                        && (!valores.observaciones || typeof(valores.observaciones) == 'undefined')){
                            errores.observaciones = 'Debe ingresar al menos un campo'
                        }


                        return errores
                    }}
                    onSubmit={(valores, {resetForm}) => {
                        enviarDatos(valores);
                    }}
                >

                {({ errors }) => (
                    <Form>
            
                           
            <div className="block-header block-header-default">
                    <h3 className="block-title">Filtro de búsqueda</h3>
                    </div> 
                    <div className="block block-rounded block-bordered">
              
                        <div className="block-content block-content-full">
                            <div className="row">
                                
                               
                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="tipoExpediente">Tipo Expediente</label>
                                        <Field as="select" className="form-control" id="tipoExpediente" name="tipoExpediente">
                                            <option value="">Por favor seleccione</option>
                                            { respuestaTipoExpediente ? selectTipoExpediente() : null }
                                        </Field>
                                        <ErrorMessage name="tipoExpediente" component={() => (<span className="text-danger">{errors.tipoExpediente}</span>)} />
                                    </div>
                                </div>

                                <div className="col">
                                    <div className="form-group">
                                        <label htmlFor="tipoQueja">Tipo Queja</label>
                                        <Field as="select" className="form-control" id="tipoQueja" name="tipoQueja">
                                            <option value="">Por favor seleccione</option>
                                            { respuestaTipoQueja ? selectTipoQueja() : null }
                                        </Field>
                                        <ErrorMessage name="tipoExpediente" component={() => (<span className="text-danger">{errors.tipoQueja}</span>)} />
                                    </div>
                                </div>
                            </div>

                            <div className="row">

                                <div className="col">
                                    <div className="form-group">
                                        <label>Observaciones</label>
                                        <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4" 
                                                placeholder="Escriba en este espacio la descripción"></Field>
                                        <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                               
                              
                            </div>
                            
                        </div>
                        <div className="block-content block-content-full text-right bg-light">
                            <button type="submit" className="btn btn-sm btn-primary" >Buscar</button>           
                        </div>
                
                    </div>
                    </Form>
                )}
            </Formik> 

            <div className="block-header block-header-default">
                <h3 className="block-title">Historial clasificaciones del rádicado (RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
            </div> 

            <div className="block block-rounded block-bordered">
                        
                <div className="block-content block-content-full">
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">No.</th>
                                        <th>Tipo de expediente</th>
                                        <th>Registrado En</th>
                                        <th>Registrado por</th>
                                        <th>Fecha de registro</th>
                                        <th>Estado actual</th>
                                        <th>Termino</th>
                                    
                                    </tr>
                                </thead>
                                <tbody>
                                    { respuestaClasificacionRadicado ? listaClasificacionRadicado() : null }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    );

  
}
export default ClasificacionRadicadoLista;
