import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';

function VigenciaLista() {
    const [vigenciaLista, setVigenciaLista] = useState({ data: [] });
    const [vigenciaListaTotal, setVigenciaListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Vigencia',
            selector: vigencia => vigencia.attributes.vigencia,
            sortable: true,
            width: "40%"
        },
        {
            name: 'Estado',
            selector: vigencia => (vigencia.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {

            cargarVigencias(1, 10);

        }
        fetchData();
    }, []);

    const cargarVigencias = (page, perPage) => {
        GenericApi.getAllGeneric('vigencia/vigencia-paginate' + '/' + page + '/' + perPage).then(

            datos => {
                if (!datos.error) {
                    setVigenciaLista(datos);
                    setVigenciaListaTotal(datos);
                }
                else
                    window.showModal()
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
        cargarVigencias(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {


        setPerPage(newPerPage);
        setPageActual(page);
        cargarVigencias(page, newPerPage);

    }

    const vigencia = () => {
        return (
            vigenciaLista.data.map((vigencia, i) => {
                return (
                    <tr key={vigencia.id}>
                        <td>{vigencia.attributes.vigencia}</td>
                        <td>
                            {vigencia.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${vigencia.id}`}>
                                <button type="button" className="btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>
                            {/* <button type="button" className="btn btn-danger mr-1 mb-3">
                                <i className="fa fa-fw fa-trash-alt"></i>
                            </button> */}
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setVigenciaLista(vigenciaListaTotal);
            }
            else {
                var filteredData = {
                    data: vigenciaListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.vigencia
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()
                            ))
                    )
                };
                setVigenciaLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}

            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de vigencias</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Vigencias registradas</h3>
                    <Link to={`/Vigencia/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar vigencia</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={vigenciaLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={vigenciaLista.meta ? vigenciaLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default VigenciaLista;
