import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import Spinner from '../Utils/Spinner';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
import { Navigate } from "react-router-dom";
import moment from 'moment';
import { useLocation } from 'react-router-dom';
import ProcesoDiciplinarioApi from '../Api/Services/ProcesoDiciplinarioApi';
import { hasAccess } from '../../components/Utils/Common';
import InfoExitoApiTextsCustom from '../Utils/InfoExitoApiTextsCustom';
import InfoExitoCierreEtapa from '../Utils/InfoExitoCierreEtapa';
import InfoErrorApi from '../Utils/InfoErrorApi';
import '../Utils/Constants';
import GenericApi from '../Api/Services/GenericApi';
import { getUser } from '../../components/Utils/Common';
import ModalCierreEtapaInfo from '../Utils/Modals/ModalCierreEtapaInfo';


import { useNavigate } from 'react-router-dom';

function RamasProceso() {
    

    const [getRespuestaAntecedente, setRespuestaAntecedente] = useState(false);

    const [isRedirect, setIsRedirect] = useState(false);
    const [getExitoApiTextsCustom, setExitoApiTextsCustom] = useState({ titulo: null, cuerpo: null, boton: null });
    const [getSelectedTipoHerramienta, setSelectedTipoHerramienta] = useState("");
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");
    const [getRtaJefe, setRtaJefe] = useState(false);
    const [getColorAntecendentes, setColorAntecendentes] = useState(global.Constants.BOTONES_RAMAS.PRIMARY);
    const [getRtaLista, setRtaLista] = useState(false);
    const [getListaFasesHabilitadas, setListaFasesHabilitadas] = useState({ data:[]});



    const [getFaseAntecedente, setFaseAntecedente] = useState(false);

    const navigate = useNavigate();


    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} />;
    }

    useEffect(() => {
        async function fetchData() {

            window.showSpinner(true); 

            // VALIDA SI ES JEFE
            jefeDependencia();
            listaFasesHabilitadas();

            
 
        }
        fetchData();
    }, []);


    /**
     * Se consulta si el usuario que se encuentra en sesión es jefe de la dependencia
     */
     const jefeDependencia = () => {

        const data = {
            "data":{
            "type":"usuario",
            "attributes":{
                "":""}
            }
        }

        GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data).then(

            datos => {
                if (!datos.error) {
                    setRtaJefe(true)
                }
                else {
                    setRtaJefe(false);
                }
            }
        )
    }


    const listaFasesHabilitadas = () => {

        GenericApi.getGeneric("proceso-diciplinario/get-fases-registradas/"+procesoDisciplinarioId).then(

            datos => {

                if (!datos.error) {
                    setListaFasesHabilitadas(datos); 
                    setFaseAntecedente(true);
                    window.showSpinner(false); 
                }
                else {
                   
                }
            }
        )
    }

    //Funciones
    const handleClicSoporteRadicado = (id_etapa, id_fase, es_soporte) => {
        from.id_etapa = id_etapa;
        from.id_fase = id_fase;
        changeValueEsFuncion(es_soporte);
    };

    function changeValueEsFuncion(es_funcion) {
        from.es_soporte = es_funcion;
    }

    const cerrarEtapa = (datos, etapa) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "descripcion": "Cierre de etapa",
                    "created_user": getUser().nombre,
                }
            }
        }
        CierreEtapaApi.addCierreEtapa(data, etapa).then(
            datos => {

                if (!datos.error) {

                    setExitoApiTextsCustom(
                        {
                            titulo: 'Asignación del proceso disciplinario',
                            cuerpo: 'El proceso disciplinario es asignado a ' + datos.data.attributes.funcionario_asignado.nombre +' '+datos.data.attributes.funcionario_asignado.apellido,
                            boton: 'Aceptar',
                            destino: 'MisPendientes'
                        }
                    );

                    //setEvaluacionRepartoCerrada(true);
                    window.showModal(7);

                }
                else {
                    //setErrorApi(datos.error.toString())

                    window.showModal(1)
                }
            }
        )
    }

    const componentAntecedente = () => {


        return (
            <>

                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) :: {radicado}</h3>
                        <span><strong>Antecedente: </strong></span><br />
                        <span title={(from.antecedente) ? from.antecedente : ""}>{from.antecedente ? from.antecedente.substring(0, 300) : ""}...</span><br />

                        <br />
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>{from.registradoPor ? from.registradoPor : "Sin Información"}</span></div>
                            <hr />
                            <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {from.dependendencia ? from.dependendencia : "Sin Información"}</span></div>
                            <hr />
                        </div>

                        <div className="row">

                            <div className="col-lg-4"><span><strong>Vigencia: </strong>{vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de registro: </strong> {moment(from.fechaRegistro, 'DD/MM/YYYY, h:mm:ss a').format("DD/MM/YYYY, h:mm:ss a")}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de ingreso: </strong> {moment(from.fechaIngreso, 'YYYY/MM/DD').format("DD/MM/YYYY")}</span></div>


                        </div>

                    </div>
                </div>
            </>
        )
    }


    const cargarFaseCR = (id_etapa, id_fase, es_soporte, redirect) => {

        for(var i = 0; i<getListaFasesHabilitadas.data.length; i++){

            if(getListaFasesHabilitadas.data[i].id == id_fase && getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <>
                        <div className='w2d-form-group'>
                            <Link to={redirect} state={{ from: from }}>
                                <button type="button" className={global.Constants.BOTONES_RAMAS.SUCCESS} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button> 
                            </Link>

                            <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-folder-open"></i></button>
                            </Link>
                        </div> 
                    </>  
                )
            }

            else if(getListaFasesHabilitadas.data[i].id == id_fase && !getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <>
                        <div className='w2d-form-group'>
                            <Link to={redirect} state={{ from: from }}>
                                <button type="button" className={global.Constants.BOTONES_RAMAS.PRIMARY} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button> 
                            </Link>

                            <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-folder-open"></i></button>
                            </Link>
                        </div>
                    </>
                )
            }
        }

    }


    const cargarSoportesRadicado = (id_etapa, id_fase, es_soporte, redirect) => {

        for(var i = 0; i<getListaFasesHabilitadas.data.length; i++){

            if(getListaFasesHabilitadas.data[i].id == id_fase && getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <div className='w2d-form-group'>
                        <Link to={redirect} state={{ from: from }}>
                            <button type="button" className={global.Constants.BOTONES_RAMAS.SUCCESS} onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-archive"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                        </Link>
                    </div>
                )
            }
            else if(getListaFasesHabilitadas.data[i].id == id_fase && !getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <div className='w2d-form-group'>
                        <Link to={redirect} state={{ from: from }}>
                        <button type="button" className={global.Constants.BOTONES_RAMAS.PRIMARY} onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-archive"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                        </Link>
                    </div>
                )
            }
        }   
    }


    const cargarCierreEtapaCR = () => {

        for(var i = 0; i<getListaFasesHabilitadas.data.length; i++){

            if(getListaFasesHabilitadas.data[i].id == global.Constants.FASES.LISTA_PARA_CIERRE_CAPTURA_REPARTO){
                return(
                    <div className="form-group">

                        <Formik
                            initialValues={{
                                inputCierreEtapa: '',
                                inputEtapa: global.Constants.ETAPAS.CAPTURA_REPARTO,
                            }}
                            enableReinitialize
                            validate={(valores) => {

                                let errores = {}
                                if (!valores.inputCierreEtapa) {
                                    errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                }
                                return errores
                            }}

                            onSubmit={(valores, { resetForm }) => {
                                cerrarEtapa(valores, null);
                            }}
                        >

                            <Form>
                                <div className="col-md-12">
                                    <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa" />
                                    <label className="form-check-label" htmlFor="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                </div>

                                <div className="col-md-12">
                                    <>
                                        {
                                            (hasAccess('CR_CierreEtapa', 'Crear')) ? (
                                                <div className="block-content block-content-full text-center">
                                                    <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                </div>
                                            ) : null
                                        }
                                    </>
                                </div>
                            </Form>
                        </Formik>
                    </div>
                )
            }

            if(getListaFasesHabilitadas.data[i].id == global.Constants.FASES.CIERRE_CAPTURA_REPARTO && getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                <div className="col-md-12 text-center">
                    <button type='button' title='Consultar cierre de etapa' className='btn btn-sm'
                        data-toggle="modal" data-target={'#modal-consultar-detalle'}> <i className="fas fa-clipboard-list fa-2x text-info "></i></button>
                </div>
                )
            }      
        }   
    }

    const cargarFaseEV = (id_etapa, id_fase, es_soporte, redirect) => {

        for(var i = 0; i<getListaFasesHabilitadas.data.length; i++){

            if(getListaFasesHabilitadas.data[i].id == global.Constants.FASES.CIERRE_CAPTURA_REPARTO && getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <>
                        <div className='w2d-form-group'>
                            <Link to={redirect} state={{ from: from }}>
                                <button type="button" className={global.Constants.BOTONES_RAMAS.SUCCESS} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button> 
                            </Link>

                            <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-folder-open"></i></button>
                            </Link>
                        </div> 
                    </>  
                )
            }

            else if(getListaFasesHabilitadas.data[i].id == id_fase && !getListaFasesHabilitadas.data[i].attributes.estado){
                return(
                    <>
                        <div className='w2d-form-group'>
                            <Link to={redirect} state={{ from: from }}>
                                <button type="button" className={global.Constants.BOTONES_RAMAS.PRIMARY} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button> 
                            </Link>

                            <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-folder-open"></i></button>
                            </Link>
                        </div>
                    </>
                )
            }
        }

    }





    return (
        <>
            {<Spinner />}
            {isRedirect ? redirectToRoutes() : null}
            {<ModalCierreEtapaInfo object={1} />}
 
            <div className="row">

                <div className="col-md-12">
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li className="breadcrumb-item">
                                    <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                            </ol>
                        </nav>

                    </div>
                </div>
                {getRespuestaAntecedente ? componentAntecedente() : null}

                {getRtaJefe? (

                    <div className="col-md-12 w2d-enter">

                        <div id="horizontal-navigation-hover-normal" class="d-none d-lg-block mt-2 mt-lg-0">
                            <ul class="nav-main nav-main-horizontal nav-main-hover">

                                <li class="nav-main-item">
                                    <a class="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                        <span class="nav-main-link-name"><i class="fas fa-tools"></i> Herramientas</span>
                                    </a>
                                    <ul class="nav-main-submenu">
                                        <li class="nav-main-item">
                                            <Link className="nav-main-link" to="/ValidarClasificacionRadicadoForm" state={{ from: from }}>
                                                <span class="nav-main-link-name">Reclasificación del expediente</span>
                                            </Link>
                                        </li>
                                        <li class="nav-main-item">
                                            <Link className="nav-main-link" to="/TipoConductaForm" state={{ from: from }}>
                                                <span class="nav-main-link-name">Tipo de conducta</span>
                                            </Link>
                                        </li>
                                    </ul>
                                </li>
                            </ul>
                        </div> 
                    </div>
                    ):null
                }
                
                {/*CAPTURA Y REPARTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">


                        <div className="block-header block-header-default">
                            <h3 className="block-title">Captura y Reparto</h3>
                            <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm" style={{ color: 'white' }} onClick={() => handleClicSoporteRadicado(1, 1, true)}><i className="fas fa-folder-open fa-2x"></i></button>
                            </Link>
                        </div>
                        <>
                            {
                                (hasAccess('CapturaYReparto', 'Consultar')) ? (
                                    <div className="block-content">

                                        <ul className="nav-items push">

                                            <li>{getFaseAntecedente? cargarFaseCR(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.ANTECEDENTES, true, '/AntecedentesLista/'):null}</li>
                                            <li>{getFaseAntecedente? cargarFaseCR(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.INTERESADO, true, '/DatosInteresadoLista/'):null}</li>
                                            <li>{getFaseAntecedente? cargarFaseCR(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.CLASIFICACION,true,'/ClasificacionRadicadoLista/'):null}</li>
                                            <li>{getFaseAntecedente? cargarFaseCR(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.ENTIDAD_INVESTIGADO, true, '/EntidadInvestigadoLista/'):null}</li>
                                            <li>{getFaseAntecedente? cargarSoportesRadicado(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.SOPORTE_RADICADO, false, '/SoporteRadicadoLista/'):null}</li>
                                            <li>{/*getFaseAntecedente? cargarCierreEtapaCR():null*/}</li>
                                        </ul>
                                    </div>
                                ) : null
                            }
                        </>


                    </div>
                </div>

                {/*EVALUACIÓN*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default" >
                            <h3 className="block-title">Evaluación</h3>
                            <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm" onClick={() => handleClicSoporteRadicado(1, 1, true)}><i className="fas fa-folder-open fa-2x"></i></button>
                            </Link>
                        </div>

                        <>
                           
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN PD*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Evaluación en PD</h3>
                        </div>
                        <>
                            {
                                (hasAccess('EvaluacionPD', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN PRELIMINAR*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Preliminar</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionPreliminar', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN DISCIPLINARIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Disciplinaria</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionDisciplinaria', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Causa / Juzgamiento</h3>
                        </div>
                        <>
                            {
                                (hasAccess('CausaJuzgamiento', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Proceso Verbal</h3>
                        </div>
                        <>
                            {
                                (hasAccess('ProcesoVerbal', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*SEGUNDA INSTANCIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Segunda Instancia</h3>
                        </div>
                        <>
                            {
                                (hasAccess('SegundaInstancia', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                


            </div>

        </>
    );
}


export default RamasProceso;
