import React, { useEffect, useState } from 'react';
import GenericApi from '../../Api/Services/GenericApi';

function ModalListaVersionesGestorRespuestaAdministracion(props) {

    const [getFuncionalidadesListModal, setFuncionalidadesListModal] = useState([]);
    const [errorApi, setErrorApi] = useState('');
    
    useEffect(() => {
        async function fetchData() {
            GenericApi.getAllGeneric('mas-orden-funcionario/historico/'+props.object).then(
                data => {
                    if (!data.error) {
                        setFuncionalidadesListModal(data);
                    }
                    else {
                        setErrorApi(data.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);

    
    const componenteListaHistorica = () => {
        
        const lista = [];
        const listaTemporal = getFuncionalidadesListModal;
        for(let dato in listaTemporal) {

            listaTemporal[dato].sort(function (a, b) {
                if (a.orden > b.orden) {
                  return 1;
                }
                if (a.orden < b.orden) {
                  return -1;
                }
                return 0;
              });

            lista.push(
                <>
                    <h4>Fecha registro: { listaTemporal[dato][0].fecha_registro }</h4>
                    <table className='table table-bordered table-striped table-vcenter js-dataTable-full'>
                        <thead>
                            <tr>
                                <th>
                                    Nombre
                                </th>
                                <th>
                                    Orden
                                </th>
                                <th>
                                    Grupo
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                listaTemporal[dato].map((funcionalidad, i) => {
                                    return <tr>
                                        <td>
                                            { funcionalidad.nombre }
                                        </td>
                                        <td>
                                            { funcionalidad.orden }
                                        </td>
                                        <td>
                                            { funcionalidad.grupo }
                                        </td>
                                    </tr>
                                })
                            }
                        </tbody>
                    </table>
                    <br></br>
                </>
            )
        }

        return lista;
    }

    return (
        <>
            <div className="modal fade" id="modal-block-popout-versiones-gestor-respuesta-administracion" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                <div className="modal-dialog modal-xl modal-dialog-popout" role="document">
                    <div className="modal-content">
                        <div className="block block-themed block-transparent mb-0">
                            <div className="block-header bg-primary-dark">
                                <h3 className="block-title">Historico de lista de { props.nombre }</h3>
                                <div className="block-options">
                                    <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                        <i className="fa fa-fw fa-times"></i>
                                    </button>
                                </div>
                            </div>
                            <div className="block-content">
                                { componenteListaHistorica() }
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal">{ 'Cerrar' }</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default ModalListaVersionesGestorRespuestaAdministracion;
