}catch(error){}};var listaDetalleCambios=function listaDetalleCambios(){if(getListaDetalleCambios.data!=null&&typeof getListaDetalleCambios.data!='undefined'){return getListaDetalleCambios.data.map(function(cambio,i){return/*#__PURE__*/_jsxs("tr",{children:[/*#__PURE__*/_jsx("td",{children:cambio.attributes.created_user}),/*#__PURE__*/_jsx("td",{children:cambio.attributes.dependencia_origen?cambio.attributes.dependencia_origen.nombre:""}),/*#__PURE__*/_jsx(_Fragment,{children:cambio.attributes.descripcion.length>global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION?/*#__PURE__*/_jsxs("td",{title:cambio.attributes.descripcion,children:[cambio.attributes.descripcion.substring(0,global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION),"..."]}):/*#__PURE__*/_jsx("td",{title:cambio.attributes.descripcion,children:cambio.attributes.descripcion})}),/*#__PURE__*/_jsx("td",{children:cambio.attributes.created_at})]},cambio.id);});}};return/*#__PURE__*/_jsxs(_Fragment,{children:[/*#__PURE__*/_jsx(Spinner,{}),/*#__PURE__*/_jsx(ModalGen,{data:getModalState}),/*#__PURE__*/_jsx("div",{className:"block block-rounded block-bordered",children:/*#__PURE__*/_jsx("div",{className:"block-content",children:/*#__PURE__*/_jsx("nav",{"aria-label":"breadcrumb",children:/*#__PURE__*/_jsxs("ol",{className:"breadcrumb breadcrumb-alt push",children:[/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx(Link,{underline:"hover",className:"text-dark",to:"/MisPendientes",children:/*#__PURE__*/_jsx("small",{children:"Mis Pendientes"})})]}),/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx(Link,{underline:"hover",className:"text-dark",to:"/RamasProceso/",state:{from:from},children:/*#__PURE__*/_jsx("small",{children:"Ramas del proceso"})})]}),/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx("small",{children:"Documento Cierre"})]})]})})})}),/*#__PURE__*/_jsx("div",{className:"content",children:/*#__PUR