                    getListaParametros.resultados[parametro].forEach(resultado => {
                        grupos.push(resultado.grupo);
                    });
                }
                grupos = [...new Set(grupos)];
            });
    
            grupos.forEach(grupo => {
                let inputs = document.querySelectorAll('input[name="' + grupo + '"]');
                inputs.forEach(input => {
                    if (input.disabled) {
