import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { getUser, removeUserSession } from '../components/Utils/Common';


const UserHeaderOptions = () => {
    const navigate = useNavigate();
    const [isUserAuth, setIsUserAuth] = useState(false);
    const [userName, setUserName] = useState();

    useEffect(() => {
        const userName = getUser() != null ? getUser().nombre : "";
        const validUser = getUser() != null;
        setIsUserAuth(validUser);
        setUserName(userName);
    });

    const handleCerrarSesion = () => {
        removeUserSession();
        navigate("/Login");
    };

    return (
        <div>
            {isUserAuth ?
                <div class="dropdown d-inline-block">
                    <button type="button" class="btn btn-dual" id="page-header-user-dropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        <i class="fa fa-fw fa-user"></i>
                        <span class="d-none d-sm-inline-block" data-toggle="tooltip" data-placement="bottom" title="" data-original-title="Opciones">{userName}</span>
                        <i class="fa fa-fw fa-angle-down ml-1 d-none d-sm-inline-block"></i>
                    </button>
                    <div class="dropdown-menu dropdown-menu-right p-0" aria-labelledby="page-header-user-dropdown">
                        <div class="p-2">
                            {/* <a class="dropdown-item" >
                                <i class="far fa-fw fa-user mr-1"></i> Cambiar contraseña
                            </a>
                            <div role="separator" class="dropdown-divider"></div> */}
                            <a class="dropdown-item" onClick={handleCerrarSesion} >
                                <i class="far fa-fw fa-arrow-alt-circle-left mr-1"></i> Cerrar sesión
                            </a>
                        </div>
                    </div>
                </div>
                : null}
        </div>
    )
}

export default UserHeaderOptions
