import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';
import DataTable from 'react-data-table-component';
import GenericApi from './../../Api/Services/GenericApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { quitarAcentos } from '../../Utils/Common';

function ListaSemaforos() {

    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaSemaforos, setListaSemaforos] = useState({ data: [] });
    const [getLista, setLista] = useState({ data: [] });
    const [ConfirmarListaSemaforos, SetConfirmarListaSemaforos] = useState(0);
    let [getMasEvento, setMasEvento] = useState('');


    const location = useLocation();
    const { from } = location.state;

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            GenericApi.getByIdGeneric('getSemaforosPorProceso', from.procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        datos.data.forEach(element => {
                            GenericApi.getByIdGeneric('semaforo', element.attributes.id_semaforo.id).then(
                                datosSemaforo => {
                                    if (!datosSemaforo.error) {
                                        getListaSemaforos.data.push(datosSemaforo.data);
                                        SetConfirmarListaSemaforos(getListaSemaforos.data.length);
                                        
                                    }else{
                                        setModalState({ title: "Lista de Semáforos", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                                    }
                                    
                                }
                            )
                        });
                        
                        window.showSpinner(false);
                    }else{
                        setModalState({ title: "Lista de Semáforos", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                    }
                }
            )

            Calcular();
        }
        fetchData();
    }, []);

    const Calcular = () => {
        GenericApi.getByIdGeneric('getDiasTranscurridos', from.procesoDisciplinarioId).then(
            datosDiasTranscurridos => {
                if (!datosDiasTranscurridos.error) {
                    console.log(datosDiasTranscurridos);
                    setLista(datosDiasTranscurridos);
                }
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }


    const SemaforoColor = (semaforo) => {
        let color = '';
        let contador = 0;
        semaforo.attributes.pdxsemaforo.attributes.condiciones.forEach(condiciones => {
            
            console.log(contador++);

            let diasTrans = parseInt(semaforo.attributes.diasTranscurridos.slice(1), 10);
            let inicial = parseInt(condiciones.inicial, 10);
            let final = parseInt(condiciones.final, 10);

            if(diasTrans >= inicial && diasTrans <= final){
               color = condiciones.color ;
            }
        })
        
        if(color == "Rojo"){
            const nodeList = document.querySelectorAll("#row-"+contador+" #cell-5-undefined");
            for (let i = 0; i < nodeList.length; i++) {
                nodeList[i].style.backgroundColor = "red";
            }
        }else if(color == "Verde"){
            const nodeList = document.querySelectorAll("#row-"+contador+" #cell-5-undefined");
            for (let i = 0; i < nodeList.length; i++) {
                nodeList[i].style.backgroundColor = "lime";
            }
        }
        contador = contador +1;
        return color;
    }
    
    const columns = [
        {
            name: 'Nombre',
            selector: semaforo => semaforo.attributes.pdxsemaforo.attributes.id_semaforo.nombre,
            sortable: true
        },
        {
            name: 'Evento de inicio',
            selector: semaforo =>  semaforo.attributes.nombreEventoInicio,
            sortable: true
        },
        {
            name: 'Actuación que inicia el semáforo',
            selector: semaforo => semaforo.attributes.nombreMasActuacion,
            sortable: true
        },
        {
            name: 'Días',
            selector: semaforo => semaforo.attributes.diasTranscurridos.slice(1),
            sortable: true
        },
        {
            name: 'Color',
            selector: semaforo => 
            <div>
                {SemaforoColor(semaforo) == "Rojo" ? 
                (
                    <div style={{"background-color": "red", "color":"white","width":"100%","text-align": "center"}}><strong>Rojo</strong></div>
                ) 
                : SemaforoColor(semaforo) == "Amarillo" ? 
                (
                    <div style={{"background-color": "yellow", "color":"white","width":"100%","text-align": "center"}}><strong>Amarillo</strong></div>
                ) 
                : 
                (
                    <div style={{"background-color": "lime", "color":"white","width":"100%","text-align": "center"}}><strong>Verde</strong></div>
                )}
            </div>
            ,
            sortable: true
        }
    ];

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                        
                        <div className="block block-themed">

                            <div className="block-header">
                                <h3 className="block-title">Listado de semáforos</h3>
                            </div>

                            <div className="block-content block-content-full">
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="block-content block-content-full">
                                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={getLista.data.filter((suggestion) => {
                                                    return suggestion;
                                                })}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent="Sin datos"
                                                paginationTotalRows={getLista.data.length}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                defaultSortFieldId="Nombre"
                                                striped
                                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                                defaultSortAsc={false}
                                            />
                                        
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default ListaSemaforos;
