import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import SamplesApi from '../../Api/Services/SamplesApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ExternosApi from '../../Api/Services/ExternosApi';
import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
function DatosInteresadoForm() {
   
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [selectTipoInteresado, setSelectTipoInteresado] = useState("");
    
    const [selectTipoEntidad, setSelectTipoEntidad] = useState("");
    const [selectSujetoProcesal, setSelectSujetoProcesal] = useState("");
    const [formIntialValuesInteresado, setFormIntialValuesInteresado] = useState();

    const [selectedDepartamento, setSelectedDepartamento] = useState("");
    const [respuestaDepartamento, setRespuestaDepartamento] = useState(false);
    const [listaDepartamento, setListaDepartamento] = useState({ data:{} });

    const [listaCiudad, setListaCiudad] = useState({ data:{} });
    const [respuestaCiudad, setRespuestaCiudad] = useState(false);

    const [listaLocalidad, setListaLocalidad] = useState({ data:{} });
    const [respuestaLocalidad, setRespuestaLocalidad] = useState(false);

    const [listaSexo, setListaSexo] = useState({ data:{} });
    const [respuestaSexo, setRespuestaSexo] = useState(false);

    const [listaGenero, setListaGenero] = useState({ data:{} });
    const [respuestaGenero, setRespuestaGenero] = useState(false);

    const [listaOrientacionSexual, setListaOrientacionSexual] = useState({ data:{} });
    const [respuestaOrientacionSexual, setRespuestaOrientacionSexual] = useState(false);

    const [listaTipoInteresado, setListaTipoInteresado] = useState({ data:{} });
    const [respuestaTipoInteresado, setRespuestaTipoInteresado] = useState(false);

    

    const [listaTipoDocumento, setListaTipoDocumento] = useState({ data:{} });
    const [respuestaTipoDocumento, setRespuestaTipoDocumento] = useState(false);

    const [listaTipoEntidad, setListaTipoEntidad] = useState({ data:{} });
    const [respuestaTipoEntidad, setRespuestaTipoEntidad] = useState(false);

    const [listaTipoSujetoProcesal, setListaTipoSujetoProcesal] = useState({ data:{} });
    const [respuestaTipoSujetoProcesal, setRespuestaTipoSujetoProcesal] = useState(false);

    const [primerApellido, setPrimerApellido] = useState("");
    const [segundoApellido, setSegundoApellido] = useState("");
    const [primerNombre, setPrimerNombre] = useState("");
    const [segundoNombre, setSegundoNombre] = useState("");


    let { radicado, procesoDisciplinarioId } = useParams();

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to="/MisPendientes" />;
    }

    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
       if(name == "primerApellido"){
        setPrimerApellido(value);
       }
       if(name == "segundoApellido"){
           setSegundoApellido(value);
       }
       if(name == "primerNombre"){
           setPrimerNombre(value);
       }
       if(name == "segundoNombre"){
            setSegundoNombre(value);
       }
          
        
        
      }


    useEffect(() => {
        
        async function fetchData() {
            window.showSpinner(true);
            SamplesApi.getAllDepartamentos().then(
                datos => !datos.error ? (setListaDepartamento(datos), setRespuestaDepartamento(true) ) : window.showModal(1)
            )
            

            ParametrosMasApi.getAllTiposDocumento().then(
                datos => !datos.error ? (setListaTipoDocumento(datos), setRespuestaTipoDocumento(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposEntidad().then(
                datos => !datos.error ? (setListaTipoEntidad(datos), setRespuestaTipoEntidad(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllTipoSujetoProcesal().then(
                datos => !datos.error ? (setListaTipoSujetoProcesal(datos), setRespuestaTipoSujetoProcesal(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllLocalidad().then(
                datos => !datos.error ? (setListaLocalidad(datos), setRespuestaLocalidad(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllSexo().then(
                datos => !datos.error ? (setListaSexo(datos), setRespuestaSexo(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllGenero().then(
                datos => !datos.error ? (setListaGenero(datos), setRespuestaGenero(true) ) : window.showModal(1)
            )

            ParametrosMasApi.getAllOrientacionSexual().then(
                datos => !datos.error ? (setListaOrientacionSexual(datos), setRespuestaOrientacionSexual(true) ) : window.showModal(1)
            )
            
            ParametrosMasApi.getAllTipoInteresado().then(
                datos => !datos.error ? (setListaTipoInteresado(datos), setRespuestaTipoInteresado(true) ) : window.showModal(1)
            )

            const timer = setTimeout(() => {
                window.showSpinner(false);
              }, 3000);
              return () => clearTimeout(timer);
            
        }
        
        fetchData();
    }, []);

    
    const selectChangeNumeroDocumento = (e) => {

        try {
            if(e != ""){
                window.showSpinner(true);



                const data = {
                    "data" : {
                        "type": "registraduria",
                        "attributes": {
                            "cedula":"123"
                        }
                    }
                }
                ExternosApi.getRegistraduria(data,e ).then(
                    datos => {
                        if(datos["codError"]=="0"){
                            datos ? (setDataFormRegistraduria(datos) ) : window.showModal(1)
                        }
                        else{
                            console.log("no encontre en registraduria");
                            const data = {
                                "data" : {
                                    "type": "usuario_rol",
                                    "attributes": {
                                        "cedula":"123"
                                    }
                                }
                            }
                            //verificamos en SINPROC
                            ExternosApi.getsinproc(data,e).then(
                                datos => {
                                    //console.log(JSON.stringify(datos));
                                    if(datos[0] != "" && datos[0] !== undefined){
                                        console.log("atos[0] tiene datos "+datos[0]);
                                        if(datos["estado"] == false){
                                            console.log("no encontro nada");
                                            inicilizarDatos();
                                             
                                        }
                                        else
                                        { 
                                            let primerApellidoLocal = "";
                                            let segundoApellidoLocal = "";
                                            let primerNombreLocal = "";
                                            let segundoNombreLocal = "";
                                              
                                            if(datos[0]["apellido"]){
                                                console.log("encontre en siproc");
        
                                             
                                                if(datos[0]["apellido"].includes(" ")){
                                                    primerApellidoLocal = datos[0]["apellido"].split(" ")[0];
                                                    segundoApellidoLocal = datos[0]["apellido"].split(" ")[1];
                                                }
                                                else{
                                                    primerApellidoLocal=datos[0]["apellido"];
                                                }
        
                                                setPrimerApellido(primerApellidoLocal);
                                                setSegundoApellido(segundoApellidoLocal);

                                                
                                            }

                                            if(datos[0]["nombre"]){
                                                console.log("encontre en siproc");
        
                                               
                                                
                                                if(datos[0]["nombre"].includes(" ")){
                                                    primerNombreLocal = datos[0]["nombre"].split(" ")[0];
                                                    segundoNombreLocal = datos[0]["nombre"].split(" ")[1];
                                                }
                                                else{
                                                    primerNombreLocal=datos[0]["nombre"];
                                                }
        

                                                setPrimerNombre(primerNombreLocal);
                                                setSegundoNombre(segundoNombreLocal);

                                            }

                                            else{
                                                console.log("no encontro en ningun lado");
                                               inicilizarDatos();
                                            }
                                        }
                                        
                                        
                                        window.showSpinner(false);
                                    }
                                    else{
                                        console.log("no encontro en ningun lado");
                                        inicilizarDatos()
                                        window.showSpinner(false);
                                    }
                
                
                                } 
                            )
            
                              
                        }
                    }
                    
                    
                )
            }
        } catch (ex) {
            console.error("Ocurrio este error "+ex);
            inicilizarDatos();
            
        }

        
        
        
    }

    const inicilizarDatos= () => {
        setPrimerApellido("");
        setSegundoApellido("");
        setPrimerNombre("");
        setSegundoNombre("");
    }

    
    const setDataFormRegistraduria= (datos) => {
        

            setPrimerApellido(datos["primerApellido"]);
            setSegundoApellido(datos["segundoApellido"]);
            setPrimerNombre(datos["primerNombre"]);
            setSegundoNombre(datos["segundoNombre"]);
            window.showSpinner(false);
        
    }


    const selectChangeTipoInteresado = (e) => {
        setSelectTipoInteresado(e);
    }

    let selectChangeDepartamento= (e) => {
        
        setSelectedDepartamento(e);
        if(e != ""){
            
            window.showSpinner(true);
            setListaCiudad({ data: [], links: [], meta: [] });
            setRespuestaCiudad(false);
    
            const data = {
                "data" : {
                    "type": "interesado",
                    "attributes": {
                        "nombre":"1",
                        "codigo_dane":"1",
                        "id_departamento":e
                    }
                }
            }
    
            ParametrosMasApi.getAllCiudadByDepartamento(data).then(
                datos => !datos.error ? (setListaCiudad(datos), setRespuestaCiudad(true) ) : window.showModal(1)
            )
            
            
            
            const timer = setTimeout(() => {
                window.showSpinner(false);
              }, 2000);
              return () => clearTimeout(timer);
            
        }
        
    }
    const selectDepartamentos = () => {
        return(
            listaDepartamento.data.map((departamento, i) => {
                return (
                    <option key={departamento.id} value={departamento.id}>{departamento.attributes.nombre}</option>
                )
            })
        )
        
    }

    const selectCiudad = () => {
        return(
            
            listaCiudad.data.map((ciuudad, i) => {
                return (
                    <option key={ciuudad.id} value={ciuudad.id}>{ciuudad.attributes.nombre}</option>
                )
            })
        )
    }

    const selectLocalidad = () => {
        return(
            listaLocalidad.data.map((localidad, i) => {
                return (
                    <option key={localidad.id} value={localidad.id}>{localidad.attributes.nombre}</option>
                )
            })
        )
    }

    const selectSexo = () => {
        return(
            listaSexo.data.map((sexo, i) => {
                return (
                    <option key={sexo.id} value={sexo.id}>{sexo.attributes.nombre}</option>
                )
            })
        )
    }

    const selectOrientacionSexual = () => {
        return(
            listaOrientacionSexual.data.map((orientacion, i) => {
                return (
                    <option key={orientacion.id} value={orientacion.id}>{orientacion.attributes.nombre}</option>
                )
            })
        )
    }
    
    const selectTipoInteresadoLis = () => {
        return(
            listaTipoInteresado.data.map((interesado, i) => {
                return (
                    <option key={interesado.id} value={interesado.id}>{interesado.attributes.nombre}</option>
                )
            })
        )
    }
    
    
    const selectGenero = () => {
        return(
            listaGenero.data.map((genero, i) => {
                return (
                    <option key={genero.id} value={genero.id}>{genero.attributes.nombre}</option>
                )
            })
        )
    }
    

    const selectTipoDocumento = () => {
        return(
            listaTipoDocumento.data.map((tipodoc, i) => {
                return (
                    <option key={tipodoc.id} value={tipodoc.id}>{tipodoc.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoEntidades = () => {
        return(
            listaTipoEntidad.data.map((tient, i) => {
                return (
                    <option key={tient.id} value={tient.id}>{tient.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoSujetoProcesal = () => {
        return(
            listaTipoSujetoProcesal.data.map((sujeto, i) => {
                return (
                    <option key={sujeto.id} value={sujeto.id}>{sujeto.attributes.nombre}</option>
                )
            })
        )
    }

    
    


    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if(formIntialValuesInteresado){
                
            data = {
            
                "data": {
                    "type": "interesado",
                    "attributes": {
                        
                        "id_tipo_interesao": selectTipoInteresado,
                        "id_tipo_sujeto_procesal": formIntialValuesInteresado.tipoSujetoProcesal ? formIntialValuesInteresado.tipoSujetoProcesal : '-1',
                        "tipo_documento":  formIntialValuesInteresado ? formIntialValuesInteresado.tipoDocumento : '-1',
                        "numero_documento":  formIntialValuesInteresado ? formIntialValuesInteresado.numeroDocumento : '', 
                        "primer_nombre":  formIntialValuesInteresado ? (formIntialValuesInteresado.primerNombre?formIntialValuesInteresado.primerNombre : primerNombre):'NO_APLICA', 
                        "segundo_nombre":  formIntialValuesInteresado ? (formIntialValuesInteresado.segundoNombre?formIntialValuesInteresado.segundoNombre : (segundoNombre?segundoNombre:"NO_APLICA")) : 'NO_APLICA',
                        "primer_apellido": formIntialValuesInteresado ? (formIntialValuesInteresado.primerApellido?formIntialValuesInteresado.primerApellido : primerApellido) : 'NO_APLICA',
                        "segundo_apellido": formIntialValuesInteresado ? (formIntialValuesInteresado.segundoApellido?formIntialValuesInteresado.segundoApellido : (segundoApellido?segundoApellido:"NO_APLICA")) : 'NO_APLICA',
                        "id_departamento": selectedDepartamento != "" ? selectedDepartamento : '-1',
                        "id_ciudad": formIntialValuesInteresado ? (formIntialValuesInteresado.ciudad?formIntialValuesInteresado.ciudad:"-1") : '-1',
                        "direccion": formIntialValuesInteresado ? (formIntialValuesInteresado.direccionCorrespondencia?formIntialValuesInteresado.direccionCorrespondencia: "NO_APLICA") : 'NO_APLICA',
                        "id_localidad": formIntialValuesInteresado ? formIntialValuesInteresado.localidad : '',
                        "email": formIntialValuesInteresado ? formIntialValuesInteresado.correo : '',
                        "telefono_celular": formIntialValuesInteresado ? formIntialValuesInteresado.telefonoCelular : '',
