                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/DatosInteresadoForm/${radicado}/${procesoDisciplinarioId}`}><small>Datos Interesado</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-primary" aria-current="page"  to={`//DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}><small> Historial</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

            <div className="content">
                <div className="row">
                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Datos del interesado</h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="ingresoProcesoDisciplinario">Tipo de interesado <span className="text-danger">*</span></label>
                                            <select className="form-control" id="ingresoProcesoDisciplinario" name="ingresoProcesoDisciplinario" value={selectTipoInteresado} onChange={e => selectChangeTipoInteresado(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                { respuestaTipoInteresado ? selectTipoInteresadoLis() : null }
                                            </select>         
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>                
                { componentFormularioInteresado() }

                
            </div>
 
         
        </>
    )

}

export default DatosInteresadoForm;
