import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
function RamasProceso() {

    const [respuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [antecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [errorApi, setErrorApi] = useState('');
    const [cambiaColorAntecedentes, setCambiaColorAntecedentes] = useState(false);
    const [cambiaColorDatosInteresado, setCambiaColorDatosInteresado] = useState(false);
    const [cambiaColorClasificacionRadicado, setCambiaColorClasificacionRadicado] = useState(false);
    const [cambiaColorEntidadInvestigado, setCambiaColorEntidadInvestigado] = useState(false);
    const [cambiaColorSoporteRadicado, setCambiaColorSoporteRadicado] = useState(false);


    let { radicado, procesoDisciplinarioId } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            const dataAntecedente = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "antecedentes": 1,
                        "descripcion": "abc",
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_dependencia": 1,
                        "fecha_registro": "NO_APLICA"
                    }
                }
            }

            const dataRadicado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            const dataDatosInteresado = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "tipo_documento" : "0",
                        "numero_documento" : "0",
                        "primer_nombre" : "0",
                        "segundo_nombre" : "0",
                        "primer_apellido" : "0",
                        "segundo_apellido" : "0",
                        "id_tipo_interesao": "0",
                        "id_tipo_sujeto_procesal": "0",
                        "id_departamento": "0",
                        "id_ciudad": "0",
                        "direccion": "0",
                        "id_localidad": "0",
                        "email": "0",
                        "telefono_celular": "0",
                        "telefono_fijo": "0",
                        "id_sexo": "0",
                        "id_genero": "0",
                        "id_orientacion_sexual": "0",
                        "cargo": "0",
                         "tarjeta_profesional": "0",
                         "id_dependencia": "0",
                         "id_tipo_entidad": "0",
                         "nombre_entidad":"0",
                         "id_proceso_disciplinario":procesoDisciplinarioId
                    }
                }
            }

            const dataEntidadInvestigado = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "nombre_entidad": "-1",
                        "nombre_investigado": "-1",
                        "cargo": "-1",
                        "codigo": "",
                        "observaciones": "",
                        "estado": true,
                        "requiere_registro":true

                    }
                }
            }

            AntecedentesApi.getAllAntecedente(dataAntecedente, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        setAntecedentesLista(datos)
                        setCambiaColorAntecedentes(true)
                        setRespuestaAntecedente(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                }
            )

            
            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(dataRadicado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        if(datos["data"].length  > 0){
                            setCambiaColorClasificacionRadicado(true);
                        }
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }  
                }
            )


            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(dataDatosInteresado, procesoDisciplinarioId).then(
                datos => {
                    if(!datos.error){
                        if(datos["data"].length  > 0){
                        
                            setCambiaColorDatosInteresado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    
                }
            )

            EntidadInvestigadoApi.getAllEntidadInvestigadoByIdProDisciplinario(dataEntidadInvestigado, procesoDisciplinarioId).then(
                datos => {
             
                    if(!datos.error){
                        if(datos["data"].length  > 0){
                        
                            setCambiaColorEntidadInvestigado(true);
                        }
                        
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                   
                }
            )

            const timer = setTimeout(() => {
                window.showSpinner(false);
              }, 3000);
              return () => clearTimeout(timer);
        }
        fetchData();
    }, []);

    const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_funcionario": 1
                }
            }
        }

        console.log(data);
        
        CierreEtapaApi.addCierreEtapa(data).then(
            datos => {
                if(!datos.error){
                    window.showModal(2)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }

  
    const componentAntecedente = () => {

        var index = antecedentesLista.data.length-1;

        var fecha_creado = new Date(antecedentesLista.data[index].attributes.fecha_creado);
        fecha_creado = fecha_creado.getFullYear()+"-"+(fecha_creado.getMonth()+1)+"-"+fecha_creado.getDate()

        console.log("LISTA: "+fecha_creado);

        return(
            <>
            <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) - (PENDIENTE NOMBRE): {radicado}</h3>
                        <span><strong>Antecente: </strong></span><br/>
                        <span>{antecedentesLista.data[index].attributes.descripcion}</span><br/>

                        <br/>
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>PENDIENTE</span></div>
                            <hr/>
                            <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {antecedentesLista.data[index].attributes.dependencia_origen}</span></div>
                            <hr/>
                        </div>

                        <div className="row">
                            <div className="col-lg-4"><span><strong>VIGENCIA: </strong>{antecedentesLista.data[index].attributes.vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA REGISTRO: </strong> {antecedentesLista.data[index].attributes.fecha_registro}</span></div>
                            <div className="col-lg-4"><span><strong>FECHA DE INGRESO: </strong> {fecha_creado}</span></div>
                        </div>

                    </div>
                </div>

                
               
            </>
        )
    }

    return (
<>
<Spinner />
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">

                                    <li className="breadcrumb-item">
                                        <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                {  respuestaAntecedente ? componentAntecedente() : null }

                    {/*CAPTURA Y REPARTO*/}
                    <div class="col-lg-4">
                        <div class="block block-themed">
                            <div class="block-header block-header-default">
                                <h3 class="block-title">Captura y reparto</h3>
                                <p>Días desde su registro: 0</p>
                            </div>
                            <div class="block-content">
                            <ul className="nav-items push">

                                {/*ANTECEDENTES*/}
                                <li>
                                   
                                    <div className="media-body">

                                        <Link to={`/AntecedentesLista/${radicado}/${procesoDisciplinarioId}`}>
                                            {cambiaColorAntecedentes &&
                                                <button type="button" className="btn btn-sm btn-success mr-1 mb-3"><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                            }
                                            {!cambiaColorAntecedentes &&
                                                <button type="button"className="btn btn-sm btn-primary mr-1 mb-3"><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                    </div>
                        
                                </li>

                                {/*DATOS DEL INTERESADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/DatosInteresadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                            {cambiaColorDatosInteresado &&
                                                <button  type="button" className="btn btn-sm btn-success mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado"><i className="fas fa-users"></i> Datos del interesado</button>
                                            }
                                            {!cambiaColorDatosInteresado &&
                                                <button type="button" class="btn btn-sm btn-success mr-1 mb-3" data-toggle="modal" data-target="#modal-datos-interesado"><i className="fas fa-users"></i> Datos del interesado</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                    </div>
                                </li>

                                {/*CLASIFICACION DEL RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/ClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                            {cambiaColorClasificacionRadicado &&
                                                <button  type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-sitemap"></i> Clasificación del radicado</button>
                                            }

                                            {!cambiaColorClasificacionRadicado &&
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3"><i class="fas fa-sitemap"></i> Clasificación del radicado</button>                                                                                                
                                            }
                                        </Link>
                                        
                                        <button  type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                </li>

                                {/*ENTIDAD DEL INVESTIGADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                            {cambiaColorEntidadInvestigado &&
                                                <button  type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-hotel"></i> Entidad del investigado</button>
                                            }
                                            {!cambiaColorEntidadInvestigado &&
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3"><i class="fas fa-hotel"></i> Entidad del investigado</button>
                                            }
                                        </Link>

                                        <button  type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                   
                                </li>

                                {/*SOPORTE RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/SoporteRadicadoForm/${radicado}/${procesoDisciplinarioId}`}>
                                            {cambiaColorSoporteRadicado &&
                                                <button  type="button" className="btn btn-sm btn-danger mr-1 mb-3"><i class="fas fa-archive"></i> Soporte Radicado</button>
                                            }
                                            {!cambiaColorSoporteRadicado &&
                                                <button  type="button" className="btn btn-sm btn-primary mr-1 mb-3"><i class="fas fa-archive"></i> Soporte Radicado</button>
                                            }
                                            </Link><button  type="button" class="btn btn-sm btn-danger mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                </li>

                                {/*CERRAR ETAPA*/}
                                <li>

                                <div className="form-group">
                                {cambiaColorAntecedentes && cambiaColorDatosInteresado && cambiaColorClasificacionRadicado && cambiaColorEntidadInvestigado && cambiaColorSoporteRadicado && (

                                    <Formik

                                        initialValues={{
                                            inputCierreEtapa: '',
                                            inputEtapa: 1,
                                        }}
                                        enableReinitialize
                                            validate={(valores) => {

                                                let errores = {}
                                                if(!valores.inputCierreEtapa){
                                                    errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                                }
                                                return errores
                                            }}

                                            onSubmit={(valores, {resetForm}) => {
                                                cerrarEtapa(valores);
                                            }}

                                    >

                                        <Form>
                                            <div className="col-md-12">
                                                <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa"/>
                                                <label className="form-check-label" for="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                            </div>

                                            <div className="col-md-12">
                                                <div className="block-content block-content-full text-left">
                                                    <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                </div>
                                            </div>
                                        </Form>
                                    </Formik>
                                )}
                                    
                                </div>

                                </li>

                                </ul>
                            </div>
                        </div>
                    </div>

                    {/*EVALUACIÓN*/}
                    <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Evaluación</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                    {/*EVALUACIÓN EN PD*/}
                    <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Evaluación en PD</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                     {/*EVALUACIÓN EN PRELIMINAR*/}
                     <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Investigación Preliminar</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                    {/*EVALUACIÓN EN DISCIPLINARIA*/}
                    <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Investigación Disciplinaria</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                    {/*CAUSA / JUZGAMIENTO*/}
                    <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Causa / Juzgamiento</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                     {/*CAUSA / JUZGAMIENTO*/}
                     <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Proceso Verbal</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>

                     {/*SEGUNDA INSTANCIA*/}
                     <div className="col-lg-4">
                        <div className="block block-themed">
                            <div className="block-header block-header-default">
                                <h3 className="block-title">Segunda Instancia</h3>
                            </div>
                            <div className="block-content">
                                
                            </div>
                        </div>
                    </div>
                    
                <div class="modal" id="modal-archivos" tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div class="modal-dialog modal-lg" role="document">
                        <div class="modal-content">
                            <div class="block block-themed block-transparent mb-0">
                                <div class="block-header bg-primary-dark">
                                    <h3 class="block-title">Adicionar Documento a (TIPO DE RADICADO): {radicado}</h3>
                                    <div class="block-options">
                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i class="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div class="block-content">


                                <Formik>
                   
                                    <Form>
