import axios from 'axios'
import { getToken, getUser, removeUserSession, setUserSession } from '../../../components/Utils/Common';

const baseUrl = process.env.REACT_APP_API_URL;

const headers = {
    'Content-Type': 'application/vnd.api+json',
    'Accept': 'application/vnd.api+json'
}

/**
 * 
 * @param {*} data 
 * @returns 
 */
async function addCierreEtapa(data){

    const url = baseUrl + "cierre-etapa";
    const token = getToken();
    headers['Authorization'] = `Bearer ${token}`;

    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        console.log(data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });

}

async function getCierreEtapaByIdProDisciplinario(data, procesoDisciplinarioId, $id_etapa){

    const url = baseUrl + "cierre-etapa/get-cierre-etapa/" + procesoDisciplinarioId+ "/"+$id_etapa;
    const token = getToken();
    headers['Authorization'] = `Bearer ${token}`;
    return await axios.post(url, data, {
        headers: headers
    })
    .then(response => {
        console.log(data);
        return response.data;
    })
    .catch(error => {
        console.log(error);
        return {estado: false, error: error}
    });
}

export default { addCierreEtapa, getCierreEtapaByIdProDisciplinario }
