import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import moment from 'moment';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';

function ParametroLista() {
    const [parametroLista, setParametroLista] = useState({ data: [] });
    const [parametroListaTotal, setParametroListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const columns = [
        {
            name: 'MÃ³dulo',
            selector: ciudad => ciudad.attributes.modulo,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Nombre',
            selector: ciudad => ciudad.attributes.nombre,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Valor',
            selector: ciudad => ciudad.attributes.valor,
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            cargarParametros(1, 10);
        }
        fetchData();
    }, []);

    const cargarParametros = (page, perPage) => {

        GenericApi.getAllGeneric('parametro/parametro-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setParametroLista(datos);
                    setParametroListaTotal(datos);
                }
                else
                    window.showModal()
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
        cargarParametros(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {

        setPerPage(newPerPage);
        setPageActual(page);
        cargarParametros(page, newPerPage);

    }

    const parametro = () => {
        return (
            parametroLista.data.map((parametro, i) => {
                return (
                    <tr key={parametro.id}>
                        <td>{parametro.attributes.modulo}</td>
                        <td>{parametro.attributes.nombre}</td>
                        <td>{parametro.attributes.valor}</td>
                        <td>
                            <Link to={`${parametro.id}`}>
                                <button type="button" className="btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>
                            {/* <button type="button" className="btn btn-danger mr-1 mb-3">
                                <i className="fa fa-fw fa-trash-alt"></i>
                            </button> */}
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setParametroLista(parametroListaTotal);
            }
            else {
                var filteredData = {
                    data: parametroListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.modulo
                            + suggestion.attributes.nombre
                            + suggestion.attributes.valor
                        ))
                    )
                };
                setParametroLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}

            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de parametros</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Parametros del sistema</h3>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={parametroLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={parametroLista.meta ? parametroLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por pÃ¡gina' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ParametroLista;
