    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function compararNombresArchivos(list){
        if(getListNombresArchivos?.data.length > 0){
            let existe = false;
            let nombre = "";
            list.forEach(
                (archivo, index) => {
                
                    let nombre_servidor = getListNombresArchivos.data.find(dato => dato.attributes.nombre_archivo == archivo.archivo.name);

                    if(nombre_servidor){
                        nombre = nombre_servidor.attributes.nombre_archivo;
                        existe = true;
                        return
                    }

                }
            );        
            
            if(existe){
                setErrorApi("El nombre del archivo '" + nombre + "', ya es utilizado por otro documento registrado en el sistema, por favor elimine el archivo y vuelva a subirlo con un nombre distinto")
                window.showModal(1)
            }
        }
    }

    function obtenerPesoTotalArchivos(list){
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if(archivo.archivo.size){
                    peso += archivo.archivo.size;
                }
            }
        )

        if(peso > 15000000){
            setErrorApi('El peso/tamaÃ±o de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tamaÃ±o y elimine algunos adjuntos, (Si desea enviar mas archivos realice otro proceso de registro de Soportes del radicado)')
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];    
        const i = Math.floor(Math.log(bytes) / Math.log(k));    
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

    function handleListCompulsa(documento, position, value) {
        if (value) {
            setListaCompulsaCopias([...getListaCompulsaCopias, documento]);
        }
        else {
            let lista_aux = getListaCompulsaCopias;
            lista_aux = lista_aux.filter(data => data.id != documento.id);
            setListaCompulsaCopias(lista_aux);
        }
    }

    const handleClicArchivo = (documento_sirius, es_compulsa) => {
        try {
            let nombre_documento = documento_sirius.id;
            let extension = documento_sirius.attributes.extension;
            let radicado_archivo = radicado;
            let vigencia_archivo = vigencia;

            console.log("Prueba de descarga", documento_sirius);

            if(getVigenciaExpediente || getRadicadoExpediente){
                radicado_archivo = getRadicadoExpediente;
                vigencia_archivo = getVigenciaExpediente;
                es_compulsa = false;
            }

            if (documento_sirius.attributes.compulsa) {
                es_compulsa = true;
                radicado_archivo = radicado;
                vigencia_archivo = vigencia;
                nombre_documento = documento_sirius.attributes.compulsa.id_documento_sirius_compulsa;
            }

            const data = {
                "data": {
                    "type": "documeto_sirius",
                    "attributes": {
                        "radicado": radicado_archivo,
                        "vigencia": vigencia_archivo,
                        "id_documento_sirius": nombre_documento,
                        "extension": extension,
                        "es_compulsa": es_compulsa
                    }
                }
            }

            DocumentoSiriusApi.getDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        //console.log(datos.content_type);
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento, extension);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName + "." + extension;
        downloadLink.click();
    }

    function buscarExpediente(expediente, vigencia) {
        setRadicadoExpediente(expediente);
        setVigenciaExpediente(vigencia);
        window.showSpinner(true);
        setRespuestaSoporteRadicado(false)
        DocumentoSiriusApi.getDocumentacionSiriusByExpedienteAndVigencia(expediente, vigencia).then(
            datos => {
                if (!datos.error) {
                    setSoporteRadicadoLista(datos);
                    setRespuestaSoporteRadicado(true);
                    //window.showModal(6);
                }
                else {
                    setErrorApi(datos.error.toString());
                    window.showModal(1);
                }
                window.showSpinner(false);
            }
        )
    }

