                                    <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                        <Field type="radio" className="custom-control-input" id="compulsa_copias1" name="requiere_compulsa" value="true" />
                                        <label className="custom-control-label" htmlFor="compulsa_copias1" name="compulsa_copias">Si</label>
                                    </div>
                                    <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                        <Field type="radio" className="custom-control-input" id="compulsa_copias2" name="requiere_compulsa" value="false" />
                                        <label className="custom-control-label" htmlFor="compulsa_copias2">No</label>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {
                            values.requiere_compulsa == 'true'
                                ?
                                (
                                    <>
                                        <div className='row'>
                                            <div className='col-md-6'>
                                                <div className="form-group">
                                                    <label htmlFor="expediente">No radicado SIRIUS</label>
                                                    <Field className="form-control" id="expediente" name="expediente" placeholder="Expediente"></Field>
                                                    <ErrorMessage name="expediente" component={() => (<span className="text-danger">{errors.expediente}</span>)} />
                                                </div>
                                            </div>
                                            {/*<div className='col-md-6'>
                                                <div className="form-group">
                                                    <label htmlFor="vigencia">Vigencia</label>
                                                    <Field className="form-control" id="vigencia" name="vigencia" placeholder="Vigencia"></Field>
                                                    <ErrorMessage name="vigencia" component={() => (<span className="text-danger">{errors.vigencia}</span>)} />
                                                </div>
                                            </div>*/}

                                        </div>
                                        <div className='row'>
                                            <div className='col-md-12'>
                                                <div className='text-right'>
                                                    <button type="button" className="btn btn-primary" onClick={() => buscarExpediente(values.expediente, values.vigencia)} disabled={!values.expediente || !values.vigencia}><i className="fa fa-fw fa-search"></i> Buscar</button>
                                                </div>
                                            </div>
                                        </div>
                                        <br></br>

                                        {
                                            respuestaSoporteRadicado ?
                                                (
                                                    soporteRadicadoLista.data.length > 0
                                                        ?
                                                        (
                                                            soporteRadicadoLista.data[0].attributes.id_proceso_disciplinario != procesoDisciplinarioId
                                                                ?
                                                                (
                                                                    <>
                                                                        <div className='row'>
                                                                            <div className='col-md-12'>
                                                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                                    <thead>
                                                                                        <tr>
                                                                                            <th className="text-center">No.</th>
                                                                                            <th>Fecha de registro</th>
                                                                                            <th>Archivo</th>
                                                                                            <th>Acciones</th>
                                                                                        </tr>
                                                                                    </thead>
                                                                                    <tbody>
                                                                                        {cargarTabla()}
                                                                                    </tbody>
                                                                                </table>
                                                                            </div>
                                                                        </div>
                                                                        <br></br>
                                                                        <div className='row'>
                                                                            <div className='col-md-12'>
                                                                                <div className="form-group">
                                                                                    <Field as="textarea" className="form-control" id="descripcion_compulsa" name="descripcion_compulsa" rows="4"
                                                                                        placeholder="DescripciÃ³n de los soportes" required maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                                                                    <div className="text-right">
                                                                                        <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                                                                    </div>
                                                                                    <ErrorMessage name='descripcion_compulsa' component={() => (<span className='text-danger'>{errors.descripcion_compulsa}</span>)} />
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </>
                                                                )
                                                                :
                                                                (
                                                                    <div className="alert alert-danger alert-dismissable text-center" role="alert">
                                                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                                        <p className="mb-0"> No se consultar los documentos anexos de este mismo proceso disciplinario </p>
                                                                    </div>
                                                                )
                                                        )
                                                        :
                                                        (
                                                            <div className="alert alert-danger alert-dismissable text-center" role="alert">
                                                                <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                                <p className="mb-0"> El proceso disciplinario consultado no tiene anexos </p>
                                                            </div>
                                                        )
                                                )
                                                :
                                                null
                                        }
                                    </>
                                )
                                :
                                null
                        }
                        <div className='row'>
                            <div className="col-md-12 text-right">
                                <button type="submit" className="btn btn-primary" disabled={(values.requiere_compulsa == 'true' && getListaCompulsaCopias.length <= 0)}> <i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/RamasProceso/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>
                        <br></br>
                    </Form>
                )}
            </Formik>
        )
    }

    const componentModalListaDetalleCambios = () => {
        return(
            <div className="modal fade" id={'modal-consultar-detalle'} tabIndex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                <div className="modal-dialog modal-lg" role="document" >
                    <div className="modal-content">
                        <div className="block block-themed block-transparent mb-0">
                            <div className="block-header bg-primary-dark">
                                <h3 className="block-title">Detalle del cambio en el soporte del radicado</h3>
                                <div className="block-options">
                                    <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                        <i className="fa fa-fw fa-times"></i>
                                    </button>
                                </div>
                            </div>
                            <div className="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                Observaciones: {getSoporteSeleccionado?getSoporteSeleccionado.attributes.descripcion:""}<br /><br />
                            </div>
                            {
                                (getListaDetalleCambios.data.length > 0) ? (
                                    <div>
                                        <div className="block-content">
                                            <spam className="block-title">Historial de cambios en el soporte del radicado</spam>
                                        </div>

                                        <div className="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        <th>Usuario Registra</th>
                                                        <th>Dependencia</th>
                                                        <th>DescripciÃ³n</th>
                                                        <th>Fecha</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {listaDetalleCambios()}
                                                </tbody>
                                            </table>
                                        </div>
                                    </div>
                                ) : null
                            }


                            <div className="block-content block-content-full text-right bg-light">
                                <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    const componentRegistro = () => {
        return (
            <>
                <div className="col-md-12">
                    <div className="alert alert-warning alert-dismissable" role="alert">
                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                        <p className="mb-0"> ETAPA:   PEDIENTE - FASE:   PENDIENTE </p>
                        <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                        <p className="mb-0">
                            NO puede superar QUINCE (15) Mb de peso/tamaÃ±o.
                            Tipo/Formato permitido: 
                            {getFormatosApi ? (getFormatosApi.map((suggestion) => {
                                if (suggestion.attributes.estado == true)
                                    return suggestion.attributes.nombre;

                            })).join(' - '):null}
                        </p>
                        <p className="mb-0">
                            El nombre del archivo debe ser de mÃ¡ximo 40 caracteres.
                        </p>
                    </div>
                </div>
                <div className="col-md-12">
                    {componentForm()}
                </div>
            </>
        )
    }

    const componentLista = () => {
        return (
            <>
                <div className="col-md-12">
                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                        <thead>
                            <tr>
                                <th className="text-center">No.</th>
                                <th>Fecha de registro</th>
                                <th>Archivo</th>
                                <th>InformaciÃ³n</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cargarTablaDocumentoCierre()}
                        </tbody>
                    </table>
                </div>
            </>
        )
    }

    const cargarTabla = () => {
        return (
            soporteRadicadoLista.data.map((documento_sirius, i) => {
                return (
                    <tr key={documento_sirius.id}>
                        <td width="10%" className="text-center">{i + 1}</td>
                        <td width="20%">{documento_sirius.attributes.created_at}</td>
                        <td>
                            {documento_sirius.attributes.nombre_archivo + '.' + documento_sirius.attributes.extension}
                        </td>
                        <td>
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius, true)}><i className="fas fa-download"></i></button>
                        </td>
                        <td>
                            <div className="custom-control custom-checkbox mb-1">
                                <input type="checkbox" className="custom-control-input" id={'example-cb-custom' + i} name={'example-cb-custom' + i} onClick={event => handleListCompulsa(documento_sirius, i, event.target.checked)} />
                                <label className="custom-control-label" htmlFor={'example-cb-custom' + i}>Compulsar</label>
                            </div>
                        </td>
                    </tr>
                )
            })
        )
    }

    const cargarTablaDocumentoCierre = () => {
        return (
            getDocumentoCierreLista.data.map((documento_sirius, i) => {
                return (
                    <tr key={documento_sirius.id}>
                        <td width="10%" className="text-center">{i + 1}</td>
                        <td width="20%">{documento_sirius.attributes.created_at}</td>
                        <td>
                            {documento_sirius.attributes.nombre_archivo + "." + documento_sirius.attributes.extension}
                        </td>
                        <td>
                            {documento_sirius.attributes.compulsa ? ('Se realizÃ³ compulsa de datos del SINPROC No: ' + documento_sirius.attributes.compulsa.radicado_compulsa + ' - vigencia (' + documento_sirius.attributes.compulsa.vigencia_compulsa + ')') : 'Se adjunto'}
                        </td>
                        <td>                            
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius, false)}><i className="fas fa-download"></i></button>
                            <button type='button' title='Consultar Descripcion' className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(documento_sirius)} data-target={'#modal-consultar-detalle'}><i className="fas fa-search"></i></button>
                            { componentModalListaDetalleCambios() }
                        </td>
                    </tr>
                )
            })
        )
    }

    const componentSinDocumentos = () => {
        return (
            <div className="col-md-12 text-center">
                <label>NO SE ANEXÃ“ DOCUMENTACIÃ“N Y NO SE REALIZÃ“ PROCESO DE COMPULSA DE DATOS PARA ESTÃ‰ EXPEDIENTE</label><br></br><br></br>
            </div>
        )
    }

    const handleCallback = (childData) => {
        try {
            //cargarAntecedentes(childData);
        } catch (error) {

        }

    }

    const listaDetalleCambios = () => {
        if (getListaDetalleCambios.data != null && typeof (getListaDetalleCambios.data) != 'undefined') {
            return (
                getListaDetalleCambios.data.map((cambio, i) => {
                    return (
                        <tr key={cambio.id}>
                            <td>
                                {cambio.attributes.created_user}
                            </td>
                            <td>
                                {cambio.attributes.dependencia_origen ? cambio.attributes.dependencia_origen.nombre : ""}
                            </td>
                            <>
                                {
                                    (cambio.attributes.descripcion.length > global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION) ? (
                                        <td title={cambio.attributes.descripcion}>
                                            {cambio.attributes.descripcion.substring(0, global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION)}...
                                        </td>
                                    ) : 
                                    (
                                        <td title={cambio.attributes.descripcion}>
                                            {cambio.attributes.descripcion}
                                        </td>
                                    )
                                }
                            </>
                            <td>
                                {cambio.attributes.created_at}
                            </td>
                        </tr>
                    )
                })
            )
        }
    }

    return (
        <>
            {<Spinner />}
            {isRedirect ? redirectToRoutes() : null}
            {<InfoExitoApiCustom />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}


            <div className="block block-rounded block-bordered">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">

                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                            <li className="breadcrumb-item"> <small>Documento Cierre</small></li>
                        </ol>
                    </nav>
                </div>
            </div>

            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className='block-header'>
                                <h3 className='block-title'>SINPROC NO {radicado} ::: DOCUMENTO CIERRE</h3>
                            </div>
                            <div className='block-content'>
                                {
                                    getDocumentoCierreSinDocumentos 
                                    ?
                                        componentSinDocumentos()
                                    :                                    
                                        (
                                            getDocumentoCierreLista.data.length > 0 
                                            ?
                                                componentLista()
                                            :
                                                componentRegistro()
                                        )
                                        
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default DocumentoCierreForm;
