                    if (tipo_queja.id == 2 && from.subTipoExpediente.id_tipo_expediente != selectedTipoExpediente) {
                        return (
                            <option key={tipo_queja.id} value={tipo_queja.id}>{tipo_queja.attributes.nombre}</option>
