                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => PODER REFERENTE A SOLICITUD
        if (selectedTipoExpediente === '2') {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
