                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => QUEJA
        if (selectedTipoExpediente === '3') {

            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": datos.tipo_queja,
                        "id_termino_respuesta": null,
                        "fecha_termino": null,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
                        "id_tipo_derecho_peticion": null,
                        "oficina_control_interno": false,
                        "created_user": getNombreUsuario,
                    }
                }
            }
        }

        //TIPO EXPEDIENTE => TUTELA => TERMINO RESPUESTA = DIAS
        if (selectedTipoExpediente === '4' && datos.termino_rta === 1) {
            data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_tipo_expediente": selectedTipoExpediente,
                        "observaciones": null,
                        "id_tipo_queja": null,
                        "id_termino_respuesta": selectedTerminosRespuesta,
                        "fecha_termino": datos.fechaTermino ? datos.fechaTermino : fechaTermino,
                        "hora_termino": null,
                        "gestion_juridica": false,
                        "estado": true,
                        "id_estado_reparto": 2,
