    // COMPONENTE CONFIRMACION OFICINA DE CONTROL INTERNO DISCIPLINARIO
    const componenteTipoRespuesta = (tipo_respuesta) => {

        // CONFIRMACION OFICINA DE CONTRO INTERNO = SI
        if (tipo_respuesta === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            selectedTipoExpediente: 3,
                            tipo_queja: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.tipo_queja) {
                                errores.tipo_queja = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            console.log(valores);
                            enviarDatosControlInterno(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label htmlFor="tipo_queja">Ingrese Tipo de Queja <span className="text-danger">*</span></label>

                                <Field as="select" className="form-control" id="tipo_queja" name="tipo_queja">
                                    <option value="">Por favor seleccione</option>
                                    {/*respuestaTiposQueja ? selectTiposQueja() : null*/}

                                </Field>

                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        /// CONFIRMACION TIPO DE CLASIFICADO = NO
        else if (tipo_respuesta === 2) {
            return (
                <>
                    <div className="form-group">
                        <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                            value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoExpediente ? selectTipoExpediente() : null}
                        </select>
                    </div>


                    {/*DERECHO DE PETICION*/}
                    {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                    {/*PODER REFERENTE A SOLIICTUD*/}
                    {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                    {/*QUEJA*/}
                    {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                    {/*TUTELA*/}
                    {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}

                </>
            );
        }
    };

    // COMPOENENTE TIPO EXPEDIENTE
    const componenteTipoExpediente = (tipo_expediente) => {

        // TIPO DE EXPEDIENTE = DERECHO DE PETICION
        if (tipo_expediente === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}

                    <div className="form-group">
                        <label htmlFor="ingresoDerechoPeticion">Tipo de derecho de petición <span className="text-danger">*</span></label>
                        <select className="form-control" id="ingresoDerechoPeticion" name="ingresoDerechoPeticion"
                            value={selectedTipoDerechoPeticion} onChange={e => selectChangeTipoDerechoPeticion(e.target.value)}>
                            <option value="">Por favor seleccione</option>
                            {respuestaTipoDerechoPeticion ? selectTipoDerechoPeticion() : null}
                        </select>
                    </div>

                    {/*COPIAS*/}
                    {selectedTipoDerechoPeticion === '1' ? componenteTipoDerechoPeticion(1) : ''}
                    {/*GENERAL*/}
                    {selectedTipoDerechoPeticion === '2' ? componenteTipoDerechoPeticion(2) : ''}
                    {/*ALERTA CONTROL POLITICO*/}
                    {selectedTipoDerechoPeticion === '3' ? componenteTipoDerechoPeticion(3) : ''}
                </>
            );
        }

        // TIPO DE EXPEDIENTE = PODER REFERENTE A SOLICITUD
        else if (tipo_expediente === 2) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

