    // COMOPNENTE TIPO DERECHO DE PETICION
    const componenteTipoDerechoPeticion = (tipo_derecho_peticion) => {

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  COPIAS
        if (tipo_derecho_peticion === 1) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            tipo_derecho_peticion: tipo_derecho_peticion,
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)


                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'

                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }


                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {

                            console.log(valores);
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <div className="form-group">
                                        <label>Observaciones</label>
                                        <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                            placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                    </div>
                                </div>

                                <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  GENERAL
        else if (tipo_derecho_peticion === 2) {
            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik

                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'
                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }


                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                    <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </Field>

                                    <label>Observaciones</label>  <span className="text-danger">*</span>
                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                        placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                    <div className="text-right">
                                        <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                    </div>
                                    <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                </div>

                                <div className="block-content block-content-full text-right">

                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                    </Link>
                                </div>

                            </Form>
                        )}
                    </Formik>
                </>
            );
        }

        // TIPO DE EXPDIENTE = DERECHO DE PETICION =  ALERTA CONTROL POLITICO
        else if (tipo_derecho_peticion === 3) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            observaciones: '',
                            gestion_juridica: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(valores.observaciones.length)
                            if (!valores.observaciones) {
                                errores.observaciones = 'Debe diligenciar el campo observaciones'
                            }
                            else if (valores.observaciones.length <= getMinimoTextArea) {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'
                            }

                            if (valores.observaciones) {
                                if (containsSpecialChars(valores.observaciones))
                                    errores.observaciones = 'Tiene caracteres inválidos'
                            }

                            if (!valores.gestion_juridica) {
                                errores.gestion_juridica = 'Debe diligenciar el campo gestión juridica'
                            }

                            return errores
                        }}
                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">{alertaPorTipoDerechoPeticion(tipo_derecho_peticion)}</p>
                                    </div>

                                    <label htmlFor="ingresoTipoExpediente">Requiere gestión juridica <span className="text-danger">*</span></label>
                                    <Field as="select" className="form-control" id="gestion_juridica" name="gestion_juridica">
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </Field>


                                    <label>Observaciones</label>  <span className="text-danger">*</span>
                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                        placeholder="Escriba en este espacio las observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                    <div className="text-right">
                                        <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                    </div>
                                    <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />

                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                        <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                            <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                        </Link>
                                    </div>

                                </div>

                            </Form>
                        )}
                    </Formik>

                </>
            );
        }

    };

    const handleCallbackFechaTermino = (childData) => {
        try {
            setFechaTermino(childData)
        } catch (error) {

        }

    }

    // COMPONENTE TERMINOS DE RESPUESTA -- TIPO EXPEDIENTE TUTELA
    const componenteTerminosRespuesta = (termino_respuesta) => {

        //TERMINO TUTELA = DIAS      
        if (termino_respuesta === 1) {
            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}

                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.fechaTermino && !fechaTermino) {
                                errores.fechaTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Horas para termino</label>  <span className="text-danger">*</span>

                                {/*<DatePicker id="fechaTermino" locale='es' name="fechaTermino" dateFormat="DD/MM/YYYY" closeOnSelect={true} placeholder="dd/mm/yyyy" onChange={(date) => setFechaTermino(date)} timeFormat={false} isValidDate={disableCustomDt} />*/}
                                <DatePerson resultDiasNoLaborales={resultDiasNoLaborales} getAnosAtrasInvalidos={getAnosAtrasInvalidos} parentCallback={handleCallbackFechaTermino} id="fechaTermino" name="fechaTermino" />
                            </div>

                            <div className="block-content block-content-full text-right">

                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>

                        </Form>
                    </Formik>
                </>

            )
        }

        //TERMINO TUTELA = HORAS     
        else if (termino_respuesta === 2) {

            return (
                <>
                    {isRedirect ? redirectToRoutes() : null}
                    <Spinner />
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{
                            termino_rta: termino_respuesta,
                            horasTermino: '',
                            fechaTermino: ''
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if (!valores.horasTermino) {
                                errores.horasTermino = 'Debe seleccionar el termino de respuesta'
                            }

                            return errores
                        }}


                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="form-group">
                                <label>Horas para termino</label>  <span className="text-danger">*</span>
                                <Field type="number" min="1" max='24' id="horasTermino" name="horasTermino" className="form-control" placeholder="" />
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                            </div>

                        </Form>
                    </Formik>
                </>
            )

        }

    };

        

        return (
                

                <>
                    {
                        (hasAccess('TiposUsuario', 'Control interno disciplinario')) ?
                            (

                                <div className="block-content">

                                    <div className="row">

                                        <div className="col-md-12">
                                            <div className="form-group">
                                                <label htmlFor="ingresoTipoExpediente">Tipo de expediente <span className="text-danger">*</span></label>
                                                <select className="form-control" id="ingresoTipoExpediente" name="ingresoTipoExpediente"
                                                    value={selectedTipoExpediente} onChange={e => selectChangeTipoExpediente(e.target.value)}>
                                                    <option value="">Por favor seleccione</option>
                                                    {respuestaTipoExpediente ? selectTipoExpediente() : null}
                                                </select>
                                            </div>
                                        </div>
                                    </div>

                                    {/*DERECHO DE PETICION*/}
                                    {selectedTipoExpediente === '1' ? componenteTipoExpediente(1) : ''}
                                    {/*PODER REFERENTE A SOLIICTUD*/}
                                    {selectedTipoExpediente === '2' ? componenteTipoExpediente(2) : ''}
                                    {/*QUEJA*/}
                                    {selectedTipoExpediente === '3' ? componenteTipoExpediente(3) : ''}
                                    {/*TUTELA*/}
                                    {selectedTipoExpediente === '4' ? componenteTipoExpediente(4) : ''}

                                </div>
                            ) : null
                    }

                </>
                        
               
        );
 
    
}

export default ClasificacionRadicado;
