import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import InteresadoEntidadPermitidaApi from '../Api/Services/InteresadoEntidadPermitidaApi';
import moment from 'moment';
import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
import Autocomplete from "../Autocomplete/Autocomplete";
import Spinner from '../Utils/Spinner';
import { hasAccess } from '../../components/Utils/Common';




function EntidadesInteresadoLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [respuesEntidadesPermitidas, setRespuestaEntidadesPermitidas] = useState(false);
    const [entidadesPermitidasLista, setEntidadesPermitidasLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaPermitidasTotal, setRespuestaEntidadesPermitidasTotal] = useState(false);
    const [entidadesPermitidasListaTotal, setEntidadesPermitidasListaTotal] = useState({ data: [], links: [], meta: [] });

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadesInteresadoLista/`} />;
    }

    useEffect(() => {
        window.showSpinner(true);
        async function fetchData() {

            //window.showSpinner(true);

            if(hasAccess('EI_EntidadesPermitidasInteresado', 'Consultar')){
                ParametrosMasApi.getAllEntidadesPermitidasInteresado().then(
                    datos => !datos.error ? (setEntidadesPermitidasListaTotal(datos), setRespuestaEntidadesPermitidasTotal(true),
                        setEntidadesPermitidasLista(datos), setRespuestaEntidadesPermitidas(true), window.showSpinner(false)) : window.showModal(1)
                )
            }
            
            
        }
        fetchData();
    }, []);




    const enviarDatos = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {

                "data": {
                    "type": "mas_entidad_permitida",
                    "attributes": {
                        "estado": estado
                    }
                }
            }


            InteresadoEntidadPermitidaApi.updateInteresadoEntidadPermitida(data, id).then(
                datos => {

                    if (!datos.error) {
                        ParametrosMasApi.getAllEntidadesPermitidasInteresado().then(
                            datos => !datos.error ? (setEntidadesPermitidasListaTotal(datos), setRespuestaEntidadesPermitidasTotal(true),
                                setEntidadesPermitidasLista(datos), setRespuestaEntidadesPermitidas(true), window.showSpinner(false)) : window.showModal(1)
                        )
                        //window.showModal(2)
                        //setIsRedirect(true);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }
                    //window.showSpinner(false);
                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }

    const listaEntidadesPermitidas = () => {

        if (entidadesPermitidasLista.data != null && typeof (entidadesPermitidasLista.data) != 'undefined') {
            return (


                entidadesPermitidasLista.data.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }
        else {
            return (

                entidadesPermitidasLista.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }

    }

    const cargarTabla = (entidadPerm) => {
        return (
            <tr key={entidadPerm.id}>
                <td>{entidadPerm.attributes.id_entidad + " " + entidadPerm.attributes.nombre_entidad}</td>
                <td>{moment(entidadPerm.attributes.created_at).format("DD/MM/YYYY")}</td>
                <td>{entidadPerm.attributes.estado == '1' ? 'Activo' : 'Inactivo'}</td>
                <td>
                    <>
                        {
                            (hasAccess('EI_EntidadesPermitidasInteresado', 'Inactivar')) ? (
                                <div>
                                    {
                                        (entidadPerm.attributes.estado == 0) ? (
                                            <button type='button' title='Activar' onClick={() => enviarDatos(1, entidadPerm.id)} className='btn btn-sm btn-success'><span className="fas fa-toggle-off"> </span></button>
                                        ) : null
                                    }

                                    {
                                        (entidadPerm.attributes.estado == 1) ? (
                                            <button type='button' title='Inacivar' onClick={() => enviarDatos(0, entidadPerm.id)} className='btn btn-sm btn-danger'><span className="fas fa-toggle-off"> </span></button>
                                        ) : null
                                    }
                                </div>
                            ) : null
                        }
                    </>
                </td>
            </tr>
        )
    }


    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setEntidadesPermitidasLista(entidadesPermitidasListaTotal);
            }
            else {

                setEntidadesPermitidasLista(entidadesPermitidasListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.id_entidad + suggestion.attributes.nombre_entidad
                        + suggestion.attributes.created_at).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };


    return (
        <>
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>
                    <div className="block block-themed">
                        <div className="block-header">

                            <h3 className="block-title">Entidades permitidas</h3>
                            <>
                                {
                                    (hasAccess('EI_EntidadesPermitidasInteresado', 'Crear')) ? (
                                        <Link to={`/EntidadesInteresadoForm/`}>
                                            <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar entidad permitida</button>
                                        </Link>
                                    ) : null
                                }
                            </>

                        </div>
                        <div className="block-content">

                            <>
                                {
                                    (hasAccess('EI_EntidadesPermitidasInteresado', 'Consultar')) ? (
                                        <div>
                                            <div className='row'>

                                                <div className='col-md-12'>
                                                    <div className="form-group ">
                                                        <label htmlFor='search'>Buscar: </label>
                                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                    </div>
                                                </div>
                                            </div>


                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        <th className="text-center">Entidad</th>
                                                        <th className="text-center">Fecha registro</th>
                                                        <th className="text-center">Estado</th>
                                                        <th className="text-center">Acciones</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {respuesEntidadesPermitidas ? listaEntidadesPermitidas() : null}
                                                </tbody>
                                            </table>
                                        </div>

                                    ) : null
                                }
                            </>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    )

}

export default EntidadesInteresadoLista;
