import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import { getUser } from '../../../components/Utils/Common';
import '../../Utils/Constants';
import GenericApi from '../../Api/Services/GenericApi';
import { Link } from "react-router-dom";
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import CaratulasParametrosPlantillaForm from '../Herramientas/CaratulasParametrosPlantillaForm';
import Spinner from '../../Utils/Spinner';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

function CaratulasForm() {

    const [errorApi, setErrorApi] = useState('');
    const [getCaratula, setCaratula] = useState("");
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getCaratulaId, setCaratulaId] = useState(0);
    const [getCaratulaActual, setCaratulaActual] = useState([]);

    const location = useLocation();
    const { from, selected_id_etapa } = location.state;

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let idEtapa = from.idEtapa >= 3 ? from.idEtapa : 3;

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            getAllCaratulas();
        }
        fetchData();
    }, []);


    /**
     * Trae la lista de las carátulas.
     */
    const getAllCaratulas = () => {

        GenericApi.getGeneric("caratulas").then(
            datos => {
                if (!datos.error) {
                    // Se setean los valores al array
                    setCaratula(datos.data);
                    setCaratulaActual({ label: datos.data[0].attributes.nombre, value: datos.data[0].id });

                    // Se setea el valor del id
                    setCaratulaId(datos.data[0].id);

                    // Se deshabilita el cargando
                    window.showSpinner(false);
                } else {
                    setModalState({ title: "SINPROC No " + radicado + " :: Crear Actuaciones", message: datos.error.toString(), show: true, redirect: '/ActuacionesForm', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    }

    const enviarDatos = (datos) => {

        window.showSpinner(true);
        const actuacionId = datos.id_actuacion;
        const inputListArchivos = [];

        const data = {
            "data": {
                "type": 'actuaciones',
                "attributes": {
                    "fileBase64": inputListArchivos[0].filebase64,
                    "nombre_archivo": inputListArchivos[0].archivo.name,
                    "ext": inputListArchivos[0].ext,
                    "peso": inputListArchivos[0].archivo.size,
                }
            }
        }

        GenericApi.addGeneric("actuaciones", data).then(
            datos => {
                console.log("caratulas -> ", datos);
                // if (!datos.error) {
                //     setModalState({ title: "SINPROC No " + radicado + " :: Creacion de carátula", message: 'Registro Exitoso', show: true, redirect: '/Transacciones', from: { from, selected_id_etapa: selected_id_etapa, id_actuacion: actuacionId }, alert: global.Constants.TIPO_ALERTA.EXITO });
                // } else {
                //     setModalState({ title: "SINPROCNo " + radicado + " :: Crear " + tipoActuacion, message: datos.error.toString(), show: true, redirect: '/ActuacionesLista', from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.ERROR });
                // }
                window.showSpinner(false);
            }
        )
    }

    const onChangeCaratula = (value) => {
        setCaratulaId(value);
    }

    return (
        <>
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}
            {<Spinner />}

            <Formik
                initialValues={{
                    id_actuacion: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    if (valores.id_actuacion == 0) {
                        errores.id_actuacion = 'Debe seleccionar una actuación para registrar'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Imprimir carátula</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Imprimir carátula</strong></h3>
                            </div>

                            <div className="block-content">
                                <div className='text-right'>
                                    <Link to={`/RamasProceso/`} title='Regresar a ramas del proceso' state={{ from: from }}>
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="id_actuacion">Carátula <span className="text-danger">*</span></label>

                                            {getCaratula && getCaratula.length > 0 ?
                                                <Select
                                                    placeholder="Selecciona"
                                                    value={getCaratulaActual}
                                                    isDisabled={true}
                                                    noOptionsMessage={() => "Sin datos"}
                                                    options={getCaratula.map(e =>
                                                        ({ label: e.attributes.nombre, value: e.id })
                                                    )}
                                                    onChange={(e) => onChangeCaratula(e.value)}
                                                /> : null
                                            }
                                            <ErrorMessage name="id_actuacion" component={() => (<span className="text-danger">{errors.id_actuacion}</span>)} />
                                        </div>
                                    </div>
                                </div>

                                {/* Cuando cambie el el valor de la caratula a mayor a 1 se coloca el formulario de parametros */}
                                {getCaratulaId > 0 ? <CaratulasParametrosPlantillaForm from={from} caratulaId={getCaratulaId} /> : null}
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );

}

export default CaratulasForm;
