import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import { getUser } from '../../Utils/Common';
import '../../Utils/Constants';
import GenericApi from '../../Api/Services/GenericApi';
import { Link } from "react-router-dom";
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import ActuacionParametrosPlantillaForm from '../Herramientas/ActuacionParametrosPlantillaForm';
import Spinner from '../../Utils/Spinner';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

function FirmarDocumentoActuacion() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [errorApi, setErrorApi] = useState('');
    const [getContraseña, setContraseña] = useState("");
    const location = useLocation()
    const { from, FirmaActuacion, nombreActuacion, estadoActualActuacion, tipoActuacion } = location.state;
    const [getNombreProceso, setNombreProceso] = useState('');

    useEffect(() => {
        async function fetchData() {
            // window.showSpinner(true);
            // console.log(FirmaActuacion);
            nombreProceso();
        }
        fetchData();
    }, []);

    const nombreProceso = () => {

        GenericApi.getByIdGeneric("nombre-proceso",from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setNombreProceso(datos.data.attributes.nombre)
                }
                window.showSpinner(false);
            }
        )
    }

    const handleClicFirmarActuacion = () => {

        // Se valida que sea la misma contraseña que se esta escribiendo
        if (FirmaActuacion.attributes.id_user.password_firma_mecanica == getContraseña) {

            // Se ejecuta el cargando
            window.showSpinner(true);

            // Se inicializa el array a enviar
            let data = {
                "data": {
                    "type": "Agregar Usuario",
                    "attributes": {
                        "estado": global.Constants.ESTADO_FIRMA_MECANICA.FIRMADO,
                        "ruta_image": FirmaActuacion.attributes.id_user.firma_mecanica,
                        "nombre_documento": FirmaActuacion.attributes.id_actuacion.documento_ruta,
                        "id_actuacion": FirmaActuacion.attributes.id_actuacion.uuid,
                        "tipo_firma": FirmaActuacion.attributes.tipo_firma,
                        "id_user": FirmaActuacion.attributes.id_user.id,
                    }
                }
            }

            // Se inicializa la API a consumir
            GenericApi.updateGeneric("actuaciones/set-firmas", FirmaActuacion.id, data).then(

                // Se inicializa la variable de respuesta
                datos => {

                    // Se valida que no haya error
                    if (!datos.error) {

                        // Se ejecuta el modal de exito
                        setModalState({
                            title: "Actuaciones :: Firma de documentos",
                            message: "Documento firmado con éxito", show: true,
                            redirect: '/ActuacionesVer/' + FirmaActuacion.attributes.proceso_disciplinario.uuid + '/' + FirmaActuacion.attributes.proceso_disciplinario.id_etapa + '/1',
                            from: {
                                from: from,
                                selected_id_etapa: FirmaActuacion.attributes.proceso_disciplinario.id_etapa,
                                id: FirmaActuacion.attributes.id_actuacion.uuid,
                                nombre: nombreActuacion,
                                estadoActualActuacion: estadoActualActuacion,
                                tipoActuacion: tipoActuacion
                            },
                            alert: global.Constants.TIPO_ALERTA.EXITO
                        });

                    } else if (datos.error) {

                        // Se ejecuta el modal de error
                        setModalState({
                            title: "Actuaciones :: Firma de documentos",
                            message: datos.error,
                            show: true,
                            alert: global.Constants.TIPO_ALERTA.ERROR
                        });
                    }

                    // Se quita el cargando
                    window.showSpinner(false);
                }
            )
        } else {

            // Se quita el cargando
            window.showSpinner(false);

            // Se ejecuta el modal
            setModalState({ title: "Actuaciones :: Firma de documentos", message: "Contraseña incorrecta", show: true, redirect: '/FirmarDocumentoActuacion', from: { FirmaActuacion: FirmaActuacion, nombreActuacion: nombreActuacion }, alert: global.Constants.TIPO_ALERTA.ERROR });
        }
    }

    const changecontraseña = (e) => {
        setContraseña(e.target.value);
    }

    return (
        <>
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}
            {<Spinner />}

            <Formik
                initialValues={{

                }}
                enableReinitialize
                validate={(valores) => {

                }}
                onSubmit={() => {
                    handleClicFirmarActuacion();
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title"><strong>{getNombreProceso} :: Firma de actuación</strong></h3>
                            </div>

                            <div className="block-content">
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            Usuario: {FirmaActuacion.attributes.id_user.name}
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            Tipo de firma:
                                            {FirmaActuacion.attributes.tipo_firma == global.Constants.TIPOS_FIRMA_MECANICA.PRINCIPAL ? " Principal" : null}
                                            {FirmaActuacion.attributes.tipo_firma == global.Constants.TIPOS_FIRMA_MECANICA.FIRMO ? " Firmó" : null}
                                            {FirmaActuacion.attributes.tipo_firma == global.Constants.TIPOS_FIRMA_MECANICA.ELABORO ? " Elaboró" : null}
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="contraseña">Contraseña de firmado <span className="text-danger">*</span></label>
                                            <Field as="input" type="password" className="form-control" id="contraseña" name="contraseña" value={getContraseña} onChange={changecontraseña}></Field>
                                            <ErrorMessage name="contraseña" component={() => (<span className="text-danger">{errors.contraseña}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            * Al firmar se incluirá su firma mecánica en el documento {nombreActuacion} {FirmaActuacion.attributes.id_actuacion.auto}
                                        </div>
                                    </div>
                                    <div className="block-content">
                                        <button type="submit" className="btn btn-rounded btn-primary" >Firmar</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );

}

export default FirmarDocumentoActuacion;
