import React, { useEffect , useState } from "react";
import Spinner from '../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../Utils/Common';
import GenericApi from '../Api/Services/GenericApi';
import '../Utils/Constants';
import ModalGen from '../Utils/Modals/ModalGeneric';

function DocumentosFirmadosOPendientesDeFirma() {

    const [getListaFirmasMecanicas, setListaFirmasMecanicas] = useState({ data: [], links: [], meta: [] });
    const [getFirmasMecanicas, setFirmasMecanicas] = useState(false);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });

    useEffect(() => {
        async function fetchData() {
           //window.showSpinner(true);
           
           GenericApi.getGeneric('usuario/get-usuario-por-name/' + getUser().nombre).then(
                datos => {
                    if (!datos.error) {
                        dataFirmasMecanica(datos.data[0].id);
                    } else {

                    }
                }
            )
            
        }
        fetchData();
    }, []);
    

    const dataFirmasMecanica = (idUser) => {
        GenericApi.getGeneric("actuaciones/get-firmas-por-usuario/" + idUser).then(
            datos => {
    
                if (!datos.error) {
                    if (datos.data.length > 0) {
                        setListaFirmasMecanicas(datos);
                        setFirmasMecanicas(true);
                    }
                } else {
                }
            }
        )
    }

    const ListaFirmasMecanicas = () => {
        return (getListaFirmasMecanicas.data.map((t) => {
            var nomDocumento = t.attributes.id_actuacion.documento_ruta;
            if(t.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.ELIMINADO){
                return null;
            }else{
                return (
                    
                    <tr>
                        <td>
                            Radicado: {t.attributes.proceso_disciplinario.radicado} Vigencia: {t.attributes.proceso_disciplinario.vigencia}
                        </td>
                        <td>
                            fecha: {t.attributes.create_at} Usuario: {t.attributes.id_user.nombre + " " + t.attributes.id_user.apellido + " ("+ t.attributes.id_user.name +")"}
                        </td>
                        <td>
                        {nomDocumento.slice(19)}
                        </td>
                        <td>
                            {t.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.PENDIENTE_FIRMA ? "Pendiente firma" : null}
                            {t.attributes.estado == global.Constants.ESTADO_FIRMA_MECANICA.FIRMADO ? "Firmado" : null}
                        </td>
                        <td style={{ width: '15%' }}>
                            {t.attributes.id_user.firma_mecanica != null ? (
                                <Link className="nav-main-link text-center" to="/FirmaActuaciones" state={{FirmaActuacion: t }}>
                                    <span className="nav-main-link-name">Firmar</span>
                                </Link>
                            ) : 
                            (
                                <button type="button" className="btn btn-primary" onClick={() => setModalState({ title: " Firmar Actuacion", message: "Falta agregar firma mecanica en su perfil", show: true, redirect: "/perfil/", alert: global.Constants.TIPO_ALERTA.ERROR })}>Firmar</button>
                                
                            )}
                        </td>
                    </tr>
                )
            }
            
        }));
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <div className="row">
                <div className="col-md-12"> 
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">Documentos firmados o pendientes de firma</h3>
                        </div>
                        <div className="block-content">
                            <div className='text-right mb-2'>
                                <Link to={`/Perfil/`} title='Regresar al perfil'>
                                    <button type="button" className="btn btn-success"><i class="fas fa-backward"></i></button>
                                </Link>
                            </div>
                            <div className="row">
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Proceso disciplinario</th>
                                            <th>Solicitado Por</th>
                                            <th>Documento</th>
                                            <th>Estado</th>
                                            <th>Acciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getFirmasMecanicas ? ListaFirmasMecanicas() : null}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>    
            </div>
        </>
    )

}

export default DocumentosFirmadosOPendientesDeFirma;
