import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import DataTable from 'react-data-table-component';
import ListaBotones from '../../Utils/ListaBotones';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';

function ActuacionesLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getActuacionesListaSearch, setActuacionesListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getSeach, setSeach] = useState('');
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getEstadoLista, setEstadoLista] = useState('');
    const [getNombreEtapaSeleccionada, setNombreEtapaSeleccionada] = useState('');
    var [getArchivosListaSearch, setArchivosListaSearch] = useState({ data: [], links: [], meta: [] });

    const [getMostrarBotonAgregar, setMostrarBotonAgregar] = useState(null);

    const location = useLocation()
    const { from, selected_id_etapa } = location.state

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let idEtapa = from.idEtapa >= 3 ? from.idEtapa : 3;
    let estado = 1;

    const [getRoutes, setRoutes] = useState({
        crear_registro: "/ActuacionesForm/",
        consultar_registros: "/ActuacionesLista/",
        ver_detalle: "/ActuacionesVer/" + procesoDisciplinarioId + "/" + selected_id_etapa + "/" + estado,
        muestra_atras: true,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        selected_id_etapa: selected_id_etapa,
    });

    const handleCallback = (childData) => {
        try {
            setEstadoLista(childData == global.Constants.ESTADOS.INACTIVO ? "Inactivos" : "Activos")
            cargarActuaciones(1, paganationPerPages, childData);
        } catch (error) {

        }
    }

    const columns = [
        {

            name: 'Actuaci贸n',
            cell: actuaciones => actuaciones.attributes.nombre_actuacion,
            selector: actuaciones => actuaciones.attributes.nombre_actuacion,
            sortable: true,
            width: "20%"
        },
        {

            name: 'Informaci贸n registro',
            cell: actuaciones =>
                <div className="mt-2 mb-2 d-none d-sm-table-cell">
                    <div className='row'>
                        <div className="col-10">
                            <small><strong>Usuario:</strong> {actuaciones.attributes.usuario.nombre + " " + actuaciones.attributes.usuario.apellido + " (" + actuaciones.attributes.id_dependencia.nombre + ")"}</small><br />
                            <small><strong>Fecha de registro:</strong> {actuaciones.attributes.created_at}</small><br />
                        </div>
                    </div>
                </div>,
            selector: actuaciones => actuaciones.attributes.created_user,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Informaci贸n Aprobaci贸n/Rechazo',
            cell: actuaciones =>
                < div className="mt-2 mb-2 d-none d-sm-table-cell" >
                    <div className='row'>
                        <div className="col-10">
                            <small><strong>Usuario:</strong> {actuaciones.attributes.usuario_accion == "" ? "-" : actuaciones.attributes.usuario_accion}</small><br />
                            <small><strong>Fecha de registro:</strong> {actuaciones.attributes.updated_at}</small><br />
                        </div>
                    </div>
                </div >,
            selector: actuaciones => actuaciones.attributes.usuario_accion,
            sortable: true,
            width: "24%"
        },
        {
            name: 'Estado',
            cell: actuaciones => actuaciones.attributes.nombre_estado_actuacion,
            selector: actuaciones => actuaciones.attributes.nombre_estado_actuacion,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Auto',
            cell: actuaciones => validarAuto(actuaciones),
            sortable: true,
            width: "10%"
        },
        {
            name: 'Detalle',
            cell: actuaciones =>
                <div className='row'>
                    <div className='col-4'>
                        <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: actuaciones.id, nombre: actuaciones.attributes.nombre_actuacion, estadoActualActuacion: actuaciones.attributes.nombre_estado_actuacion, tipoActuacion: actuaciones.attributes.tipo_actuacion }} >
                            <button type='button' title='Ver actuaci贸n' className='btn btn-sm btn-primary' data-toggle="modal" ><i className="fas fa-search"></i></button>
                        </Link>
                    </div>
                </div >,
            width: "10%"
        }
    ];

    function validarAuto(actuaciones) {

        // Se captura el auto de la actuacion
        let autoActuacion = actuaciones.attributes.auto;

        // Se valida que haya un auto de actuacion
        if (autoActuacion) {

            // Se captura al actuacion id
            let actuacionId = actuaciones.id;

            // Se valida que haya una actuacion
            if (actuacionId) {

                // Buscamos el detalle de los documentos
                const element = validar(autoActuacion, actuacionId);

                console.log("element", element);
            }

            return (
                <div className='row'>
                    <div className='col-4'>
                        <div className='row'>
                            <div className='col-4'>
                                <button type='button' title={autoActuacion} className='btn btn-sm btn-primary' data-toggle="modal"><i className="fas fa-download"></i></button>
                            </div>
                        </div >
                    </div>
                </div>
            );

            // // Se retorna el div con el elemento
            // return (
            //     <div className='row'>
            //         <div className='col-4'>
            //             <button type='button' title={getArchivosListaSearch.auto} className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => handleClicArchivo(getArchivosListaSearch.attributes.id, getArchivosListaSearch.attributes.extension, getArchivosListaSearch.attributes.nombre_archivo)} ><i className="fas fa-download"></i></button>
            //         </div>
            //     </div >
            // );

        } else {
            return (
                < div className="mt-2 mb-2 d-none d-sm-table-cell" >
                    <div className='row'>
                        <div className="col-10">
                            <small><strong>-</strong></small><br />
                        </div>
                    </div>
                </div >
            );
        }
    };

    const validar = (autoActuacion, actuacionId) => {
        GenericApi.getGeneric("archivo-actuaciones/get-archivo-actuaciones-by-uuid/" + actuacionId).then(
            datos => {

                // Se recorre el array para sacar el documento definitivo
                // for (let index = 0; index < datos.data.length; index++) {

                // Se captura la informacion para validar
                if (datos.data[1]) {
                    const element = datos.data[1];
                    const idTipoArchivo = element.attributes.id_tipo_archivo;

                    console.log("idTipoArchivo -> ", idTipoArchivo);

                    if (idTipoArchivo == 2) {

                        element.auto = autoActuacion;
                        //setArchivosListaSearch(element);
                    }

                    return element;
                }


                // Se valida que el tipo sea el documentod definitivo

                //}
            }
        );
    }

    const elementoDibujado = (element) => {

        console.log("Entro a dibujar");

        // Se retorna el div con el elemento
        return (
            <div className='row'>
                <div className='col-4'>
                    <button type='button' title={element.attributes.auto} className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => handleClicArchivo(element.attributes.id, element.attributes.extension, element.attributes.nombre_archivo)} ><i className="fas fa-download"></i></button>
                </div>
            </div >
        );
    }

    const handleClicArchivo = (id_documento, extension, nombre_documento) => {

        try {
            window.showSpinner(true);

            // Se genera la peticion para descargar el archivo
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + id_documento + "/" + extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento);
                    } else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const redirectToRoutes = () => {
        return <Navigate to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos");
            cargarActuaciones(1, paganationPerPages, global.Constants.ESTADOS.ACTIVO);
            obtenerDatosDeLaEtapaSeleccionada();
        }
        fetchData();
    }, []);

    const cargarActuaciones = (page, perPage, estado) => {
        GenericApi.getGeneric('actuaciones/get-actuaciones-discipl-etapa/' + procesoDisciplinarioId + '/' + selected_id_etapa + '/' + estado).then(
            datos => {
                if (!datos.error) {

                    if (datos["data"] != "") {
                        obtenerLaUltimaActuacion(datos.data[0].attributes.id_estado_actuacion,datos.data[0].attributes.id_actuacion);
                        setActuacionesListaSearch(datos)
                    }
                } else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }

            }
        )
    }

    const obtenerLaUltimaActuacion = (estado, id) => {
        GenericApi.getByIdGeneric('mas_actuaciones',id).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"] != "") {
                        console.log(datos);
                        validacionDeBotonAgregar(datos.data.attributes.tipo_actuacion ,estado , datos.data.attributes.excluyente)
                    }
                } else {
                    window.showModal(1)
                }

            }
        )
    }

    const obtenerDatosDeLaEtapaSeleccionada = () => {
        GenericApi.getGeneric('mas-etapa/' + selected_id_etapa).then(
            datos => {
                if (!datos.error) {
                    setNombreEtapaSeleccionada(datos.data.attributes.nombre);
                } else {
                    console.log("Error en la consulta de obtener los datos de la etapa seleccionada")
                }
                
            }
        )
    }

    const handlePageChange = page => {

        //window.showSpinner(true);
        setPageActual(page);
        cargarActuaciones(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarActuaciones(page, newPerPage);

    }

    const validacionDeBotonAgregar = (tipo_actuacion, estado,excluyente) => {

        if(tipo_actuacion == 0){
            if (from.idEtapa == selected_id_etapa && excluyente != 1) {
                setMostrarBotonAgregar(true);
            } else {
                setMostrarBotonAgregar(false);
            }
        }else if(tipo_actuacion == 1){
            setMostrarBotonAgregar(false);
        }else if(tipo_actuacion == 2){
            if (from.idEtapa == selected_id_etapa && (excluyente != 1 || estado != 3)) {
                setMostrarBotonAgregar(true);
            } else {
                setMostrarBotonAgregar(false);
            }

        }else{
            setMostrarBotonAgregar(true);
        }
        
        window.showSpinner(false);
    }

    
    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Actuaciones</small></li>
                            </ol>
                        </nav>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista de actuaciones {getEstadoLista} de {getNombreEtapaSeleccionada}</strong></h3>
                        </div>
                        <div className="block-content">
                            <div className='row'>
                                <div className='col-md-3'>
                                    <div className="form-group ">
                                        <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                                    </div>
                                </div>
                                <div className='col-md-3'>
                                    <Link to="/Transacciones" state={{ from: from, selected_id_etapa: selected_id_etapa }}>
                                        <button type="button" className="btn btn-primary w-100" ><i class="fa fa-arrow-right"></i> Transacciones</button>
                                    </Link>
                                </div>
                                {getMostrarBotonAgregar != null ? (
                                    <ListaBotones getRoutes={getRoutes} from={from} mostrarBotonAgregar={getMostrarBotonAgregar} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                                ) : null}
                            </div>

                            <div className="row">
                                <div className="col-sm-12">
                                    <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                        columns={columns}
                                        data={getActuacionesListaSearch.data.filter((suggestion) => {

                                            if (getSeach === "") {
                                                return suggestion;
                                            } else if (

                                                ((suggestion.attributes.nombre_actuacion
                                                    + suggestion.attributes.created_user + suggestion.attributes.usuario_accion +
                                                    suggestion.attributes.nombre_estado_actuacion + suggestion.attributes.auto
                                                ).toLowerCase().includes(getSeach.toLowerCase()))

                                            ) {
                                                return suggestion;
                                            }
                                        })}
                                        perPage={perPage}
                                        page={pageActual}
                                        pagination
                                        noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                        paginationTotalRows={getActuacionesListaSearch.length}
                                        onChangePage={handlePageChange}
                                        onChangeRowsPerPage={handlePerRowsChange}
                                        defaultSortFieldId="Nombre"
                                        striped
                                        paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                        defaultSortAsc={false}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );

}

export default ActuacionesLista;
